import {
  Project,
  ProjectDto,
  ProjectGroupPermissionDisplayModel,
  ProjectPermissionLevel,
  ProjectUserPermissionDisplayModel,
} from '@/types/Types';
import { LoadDataParams } from '@/composables/useGenericTableState';
import API from '@/services/API';

/**
 * Returns projects, with pagination and sorting
 * Response is of the type `org.springframework.data.domain.Page` which
 *  contains other properties not listed here.
 */
async function getProjects(
  params?: LoadDataParams,
): Promise<{ content: Project[]; totalElements: number }> {
  const response = await API.get('/projects', { params: params ?? null });
  return response.data;
}

/**
 * Returns a single project by ID.
 * @param projectId the ID of the project to fetch.
 */
async function getProject(projectId: string): Promise<Project> {
  const response = await API.get(`/projects/${projectId}`);
  return response.data;
}

/**
 * Returns the most recently created/opened projects for the current user
 */
async function getRecentProjects(): Promise<Project[]> {
  const response = await API.get('/projects/recent', { params: { limit: 10 } });
  return response.data;
}

/**
 * Returns the permission level of the current user for a project.
 * @param projectId the ID of the project to check.
 */
async function getPermissionLevel(
  projectId: string,
): Promise<ProjectPermissionLevel> {
  const response = await API.get(`/projects/${projectId}/permissions/level`);
  return response.data;
}

/**
 * Gets the permission levels for multiple projects in one call.  Useful for getting all the permissions for a page
 * of projects in a single call
 * @param projectIds  The IDs of the projects to get the permission levels for
 */
async function getPermissionLevels(
  projectIds: string[],
): Promise<{ [projectId: string]: ProjectPermissionLevel }> {
  const response = await API.post('/projects/permissions/levels', projectIds);
  return response.data;
}

/**
 * Returns true if the given permission level is at least as high as the required permission level.
 * @param permissionLevel
 * @param requiredPermissionLevel
 */
function hasPermissionLevel(
  permissionLevel: ProjectPermissionLevel | undefined,
  requiredPermissionLevel: ProjectPermissionLevel,
): boolean {
  if (!permissionLevel) {
    return false;
  }
  return (
    Object.values(ProjectPermissionLevel).indexOf(permissionLevel) >=
    Object.values(ProjectPermissionLevel).indexOf(requiredPermissionLevel)
  );
}

/**
 * Deletes a project by id
 * @param projectId the ID of the project to delete
 */
async function deleteProject(projectId: string) {
  const response = await API.delete(`/projects/${projectId}`);
  return response.data;
}

/**
 * Creates a new project
 * @param projectDto  the project data to create
 */
async function createProject(projectDto: ProjectDto): Promise<Project> {
  const response = await API.post(`/projects`, projectDto, {
    headers: { 'Content-Type': 'application/json' },
  });
  return response.data;
}

/**
 * Updates a project
 * @param projectId the ID of the project to update
 * @param projectDto the project data to update
 */
async function updateProject(projectId: string, projectDto: ProjectDto) {
  const response = await API.put(`/projects/${projectId}`, projectDto, {
    headers: { 'Content-Type': 'application/json' },
  });
  return response.data;
}

/**
 * Returns a page of users with their permissions for a project.
 * @param projectId the ID of the project to fetch users for.
 * @param params    the pagination and sorting parameters.
 */
async function getUsers(
  projectId: string,
  params?: LoadDataParams,
): Promise<{
  content: ProjectUserPermissionDisplayModel[];
  totalElements: number;
}> {
  const response = await API.get(`/projects/${projectId}/users`, {
    params: params ?? null,
  });
  return response.data;
}

/**
 * Returns a page of groups with their permissions for a project.
 * @param projectId the ID of the project to fetch groups for.
 * @param params    the pagination and sorting parameters.
 */
async function getGroups(
  projectId: string,
  params?: LoadDataParams,
): Promise<{
  content: ProjectGroupPermissionDisplayModel[];
  totalElements: number;
}> {
  const response = await API.get(`/projects/${projectId}/groups`, {
    params: params ?? null,
  });
  return response.data;
}

/**
 * Updates the permission level of a user for a project.
 * @param projectId       the ID of the project to update.
 * @param userIds         the IDs of the user to update.
 * @param permissionLevel the new permission level.
 */
async function updateUserPermissions(
  projectId: string,
  userIds: string | string[],
  permissionLevel: ProjectPermissionLevel,
) {
  const response = await API.post(
    `/projects/${projectId}/permissions/user`,
    Array.isArray(userIds) ? userIds : [userIds],
    { params: { level: permissionLevel } },
  );
  return response.data;
}

/**
 * Deletes permissions for the specified project and users
 * @param projectId the ID of the project to update.
 * @param userIds   the IDs of the users to delete.
 */
async function deleteUserPermissions(
  projectId: string,
  userIds: string | string[],
) {
  const response = await API.delete(`/projects/${projectId}/permissions/user`, {
    data: Array.isArray(userIds) ? userIds : [userIds],
  });
  return response.data;
}

/**
 * Updates the permission level of a group for a project.
 * @param projectId       the ID of the project to update.
 * @param groupIds        the ID of the group to update.
 * @param permissionLevel the new permission level.
 */
async function updateGroupPermissions(
  projectId: string,
  groupIds: string | string[],
  permissionLevel: ProjectPermissionLevel,
) {
  const response = await API.post(
    `/projects/${projectId}/permissions/group`,
    Array.isArray(groupIds) ? groupIds : [groupIds],
    { params: { level: permissionLevel } },
  );
  return response.data;
}

/**
 * Deletes permissions for the specified project and groups
 * @param projectId the ID of the project to update
 * @param groupIds  the IDs of the groups to delete
 */
async function deleteGroupPermissions(
  projectId: string,
  groupIds: string | string[],
) {
  const response = await API.delete(
    `/projects/${projectId}/permissions/group`,
    { data: Array.isArray(groupIds) ? groupIds : [groupIds] },
  );
  return response.data;
}

/**
 * Get the number of documents in a list of projects
 * @param projectIds The IDs of the projects
 */
async function getDocumentCounts(projectIds: string[]): Promise<number[]> {
  const response = await API.get(`/projects/document-counts`, {
    params: {
      ids: projectIds,
    },
  });
  return response.data;
}

/**
 * Saves an image associated with a project. b64Image is a base64 encoded image data url.
 *
 * @param projectId the ID of the project
 * @param b64Image  the base64 encoded image data url
 */
async function saveImage(projectId: string, b64Image: string) {
  await API.put(`/projects/${projectId}/image`, b64Image, {
    headers: { 'Content-Type': 'text/plain' },
  });
}

/**
 * Deletes the image associated with a project
 * @param projectId the ID of the project
 */
async function deleteImage(projectId: string) {
  await API.delete(`/projects/${projectId}/image`);
}

/**
 * Crops an image associated with a project. b64Image is a base64 encoded image data url
 * @param projectId the ID of the project
 * @param b64Image  the base64 encoded image data url
 */
async function cropImage(projectId: string, b64Image: string) {
  await API.post(`/projects/${projectId}/image/crop`, b64Image, {
    headers: { 'Content-Type': 'text/plain' },
  });
}

export default {
  getProjects,
  getProject,
  getRecentProjects,
  getPermissionLevel,
  getPermissionLevels,
  hasPermissionLevel,
  deleteProject,
  createProject,
  updateProject,
  getUsers,
  updateUserPermissions,
  deleteUserPermissions,
  getGroups,
  updateGroupPermissions,
  deleteGroupPermissions,
  getDocumentCounts,
  saveImage,
  deleteImage,
  cropImage,
};

