@RestController
@RequestMapping("/api/projects")
@RequiredArgsConstructor
public class ProjectsController {

  private final ProjectDataService projectDataService;
  private final ProjectService projectService;
  private final ProjectRepository projectRepository;
  private final CurrentUserService currentUserService;
  private final UserService userService;
  private final GroupService groupService;
  private final ProjectImageService projectImageService;
  private final UserProjectPreferenceRepository userProjectPreferenceRepository;

  /**
   * Get a page of projects available for the current user
   *
   * @param page        The page number
   * @param rows        The number of rows per page
   * @param sortField   The field to sort on
   * @param sortOrder   The sort order
   * @param queryString The (optional) query string
   * @return The page of projects
   */
  @GetMapping
  public ResponseEntity<Page<Project>> getProjects(
    @RequestParam(required = false, defaultValue = "0") final Integer page,
    @RequestParam(required = false, defaultValue = "5") final Integer rows,
    @RequestParam(required = false) final String sortField,
    @RequestParam(required = false) final Integer sortOrder,
    @RequestParam(required = false, defaultValue = "") final String queryString
  ) {
    final User user = currentUserService.get();
    final Set<String> groupIds = user.getGroups() != null
      ? user.getGroups().stream().map(Group::getId).collect(Collectors.toSet())
      : Set.of();
    final Page<Project> projects = projectRepository.findProjectsForUser(
      currentUserService.get().getId(),
      groupIds,
      queryString,
      SortedPageRequest.of(sortField, sortOrder, page, rows)
    );
    return ResponseEntity.ok(projects);
  }

