     1→import { ref, watch } from 'vue';
     2→import { useRoute } from 'vue-router';
     3→import {
     4→  DataTablePageEvent,
     5→  DataTableRowClickEvent,
     6→  DataTableSortEvent,
     7→} from 'primevue/datatable';
     8→
     9→export interface LoadDataParams {
    10→  page?: number;
    11→  rows?: number;
    12→  sortField?: string;
    13→  sortOrder?: number;
    14→  queryString?: string;
    15→  filter?: string;
    16→  toggleLoading?: boolean;
    17→}
    18→
    19→const DEFAULT_PAGE = 0;
    20→export const DEFAULT_ROWS_PER_PAGE_OPTIONS = [25, 50, 100, 250, 500];
    21→
    22→export default function useGenericTableState<T>(
    23→  loadData: (params?: LoadDataParams) => Promise<{
    24→    content: T[];
    25→    totalElements: number;
    26→  }>,
    27→  onSelectionChange?: (newSelection: T[]) => void,
    28→  onRangeSelect?: (items: any[]) => void,
    29→  onPage?: (e: DataTablePageEvent) => void,
    30→  onSort?: (e: DataTableSortEvent) => void,
    31→  onRowClick?: (e: DataTableRowClickEvent) => void,
    32→  rowsPerPageOptions?: number[],
    33→  initialSortField?: string,
    34→  initialSortDirection?: number,
    35→) {
    36→  const items = ref<T[] | undefined>();
    37→
    38→  const loading = ref(false);
    39→  const totalRecords = ref(0);
    40→  const first = ref();
    41→
    42→  const route = useRoute();
    43→
    44→  const sort = ref(
    45→    route.query.sort ? String(route.query.sort) : initialSortField,
    46→  );
    47→  const sortOrder = ref(
    48→    route.query.sortOrder
    49→      ? Number(route.query.sortOrder)
    50→      : initialSortDirection,
    51→  );
    52→  const page = ref(Number(route.query.page ?? DEFAULT_PAGE));
    53→  const setSelectedPageIndex = (newPageIndex: number) => {
    54→    page.value = newPageIndex;
    55→  };
    56→
    57→  let initialRowsValue = DEFAULT_ROWS_PER_PAGE_OPTIONS[0];
    58→  if (route.query.rows) {
    59→    initialRowsValue = Number(route.query.rows);
    60→  } else if (rowsPerPageOptions) {
    61→    [initialRowsValue] = rowsPerPageOptions;
    62→  }
    63→  const rows = ref(initialRowsValue);
    64→  const queryString = ref(String(route.query.queryString ?? ''));
    65→  const selectedItems = ref<T[]>([]);
    66→  const lastClickedIndex = ref<number>(-1);
    67→
    68→  /**
    69→   * Apply the event from the primevue data table to the local state
    70→   * @param event
    71→   */
    72→  const applyDataTableEvent = (
    73→    event: DataTablePageEvent | DataTableSortEvent,
    74→  ) => {
    75→    if (sort.value !== event.sortField) {
    76→      // we're changing fields, reset the page number as primevue data table resets to page 1 when sort changes
