import { ref, watch } from 'vue';
import { useRoute } from 'vue-router';
import {
  DataTablePageEvent,
  DataTableRowClickEvent,
  DataTableSortEvent,
} from 'primevue/datatable';

export interface LoadDataParams {
  page?: number;
  rows?: number;
  sortField?: string;
  sortOrder?: number;
  queryString?: string;
  filter?: string;
  toggleLoading?: boolean;
}

const DEFAULT_PAGE = 0;
export const DEFAULT_ROWS_PER_PAGE_OPTIONS = [25, 50, 100, 250, 500];

export default function useGenericTableState<T>(
  loadData: (params?: LoadDataParams) => Promise<{
    content: T[];
    totalElements: number;
  }>,
  onSelectionChange?: (newSelection: T[]) => void,
  onRangeSelect?: (items: any[]) => void,
  onPage?: (e: DataTablePageEvent) => void,
  onSort?: (e: DataTableSortEvent) => void,
  onRowClick?: (e: DataTableRowClickEvent) => void,
  rowsPerPageOptions?: number[],
  initialSortField?: string,
  initialSortDirection?: number,
) {
  const items = ref<T[] | undefined>();

  const loading = ref(false);
  const totalRecords = ref(0);
  const first = ref();

  const route = useRoute();

  const sort = ref(
    route.query.sort ? String(route.query.sort) : initialSortField,
  );
  const sortOrder = ref(
    route.query.sortOrder
      ? Number(route.query.sortOrder)
      : initialSortDirection,
  );
  const page = ref(Number(route.query.page ?? DEFAULT_PAGE));
  const setSelectedPageIndex = (newPageIndex: number) => {
    page.value = newPageIndex;
  };

  let initialRowsValue = DEFAULT_ROWS_PER_PAGE_OPTIONS[0];
  if (route.query.rows) {
    initialRowsValue = Number(route.query.rows);
  } else if (rowsPerPageOptions) {
    [initialRowsValue] = rowsPerPageOptions;
  }
  const rows = ref(initialRowsValue);
  const queryString = ref(String(route.query.queryString ?? ''));
  const selectedItems = ref<T[]>([]);
  const lastClickedIndex = ref<number>(-1);

  /**
   * Apply the event from the primevue data table to the local state
   * @param event
   */
  const applyDataTableEvent = (
    event: DataTablePageEvent | DataTableSortEvent,
  ) => {
    if (sort.value !== event.sortField) {
      // we're changing fields, reset the page number as primevue data table resets to page 1 when sort changes
      page.value = DEFAULT_PAGE;
    }

    sort.value = (event.sortField as string) ?? sort.value;
    sortOrder.value = event.sortOrder ?? sortOrder.value;
    page.value = (event as DataTablePageEvent).page ?? page.value;
    rows.value = event.rows ?? rows.value;
    first.value = page.value * rows.value;
  };

  /**
   * Load the data from the consumer
   */
  const loadTableData = async () => {
    loading.value = true;
    first.value = page.value * rows.value;

    const { content, totalElements } = await loadData({
      page: page.value,
      rows: rows.value,
      sortField: sort.value,
      sortOrder: sortOrder.value,
      queryString: queryString.value,
    });
    items.value = content;
    totalRecords.value = totalElements;
    loading.value = false;
  };
  watch([queryString], loadTableData, { immediate: true });

  /**
   * Propagate the page event to the consumer if it's defined
   * @param event
   */
  const onPageFn = (event: DataTablePageEvent) => {
    applyDataTableEvent(event);
    if (onPage !== undefined) {
      onPage(event);
    } else {
      loadTableData();
    }
  };

  /**
   * Propagate the sort event to the consumer if it's defined
   * @param event
   */
  const onSortFn = (event: DataTableSortEvent) => {
    applyDataTableEvent(event);
    if (onSort !== undefined) {
      onSort(event);
    } else {
      loadTableData();
    }
  };

  /**
   * Propagate the selection change event to the consumer if it's defined
   */
  const onRowSelectChangeFn = () => {
    if (onSelectionChange !== undefined) {
      onSelectionChange(selectedItems.value as T[]);
    }
  };
  watch([selectedItems], onRowSelectChangeFn);

  /**
   * Handle range selection between two row indices
   * @param startIndex
   * @param endIndex
   */
  const selectRange = (startIndex: number, endIndex: number) => {
    if (!items.value) return;

    const start = Math.min(startIndex, endIndex);
    const end = Math.max(startIndex, endIndex);
    const rangeItems = items.value.slice(start, end + 1);

    // Call the range select callback if provided
    if (onRangeSelect) {
      try {
        onRangeSelect(rangeItems);
      } catch {
        // Silently handle errors in callback to prevent breaking the selection flow
      }
    }
  };

  /**
   * Propagate the row click event to the consumer
   * @param event
   */
  const onRowClickFn = (
    event: DataTableRowClickEvent,
    isShiftPressed: boolean = false,
  ) => {
    // Check if the click target is a selection checkbox to avoid conflicts
    const clickTarget = event.originalEvent.target as Element | null;
    const isClickingSelectionCheckbox =
      clickTarget?.closest('.p-checkbox') !== null;

    if (isClickingSelectionCheckbox) {
      return;
    }

    const currentIndex = event.index;

    // Handle shift+click for range selection
    if (isShiftPressed && lastClickedIndex.value >= 0 && items.value) {
      selectRange(lastClickedIndex.value, currentIndex);
      // Update last clicked index after range selection
      lastClickedIndex.value = currentIndex;
      return;
    }

    // Update last clicked index for future range selections
    lastClickedIndex.value = currentIndex;

    // Propagate regular row click event
    if (onRowClick !== undefined) {
      onRowClick(event);
    }
  };

  return {
    loadTableData,
    setSelectedPageIndex,
    items,
    loading,
    totalRecords,
    page,
    rows,
    queryString,
    first,
    sort,
    sortOrder,
    onSortFn,
    onPageFn,
    onRowClickFn,
  };
}

