import { computed, ref, watch } from 'vue';
import {
  AuditEventType,
  Project,
  ProjectDto,
  ProjectPermissionLevel,
  PropertyDescriptor,
  PropertyTag,
  RoleType,
} from '@/types/Types';
import ProjectService from '@/services/ProjectService';
import { useRoute, useRouter } from 'vue-router';
import { defineStore } from 'pinia';
import { hasRole } from '@/services/AdminService';
import PanteraAPI from '@/services/PanteraAPI';
import { hashFile } from '@/util/HashUtil';
import { LoadDataParams } from '@/composables/useGenericTableState';
import ProjectDataService from '@/services/ProjectDataService';
import Logger from '@/services/Logger';

const useProjectStore = defineStore('project', () => {
  const project = ref<Project | undefined>();
  const projectPermissionLevel = ref<ProjectPermissionLevel | undefined>();
  const propertyDescriptors = ref<PropertyDescriptor[]>([]);
  const allPropertyDescriptors = ref<PropertyDescriptor[]>([]);
  const recentProjects = ref<Project[]>([]);
  const projectDocumentIds = ref<string[]>([]);
  const totalDocumentCount = ref<number | undefined>();

  // Callbacks to execute when project loading finishes
  const projectLoadFinishCallbacks = ref<(() => void)[]>([]);

  // Callbacks to execute when project changes (for clearing state)
  const projectChangeCallbacks = ref<(() => void)[]>([]);

  const route = useRoute();
  const router = useRouter();

  /**
   * Load the most recent projects for the current user
   */
  const getRecentProjects = async () => {
    recentProjects.value = await ProjectService.getRecentProjects();
  };

  /**
   * Load a project by its ID
   * @param id  The ID of the project to load
   */
  const getProject = async (id: string) => {
    const p = await ProjectService.getProject(id);
    await PanteraAPI.createAuditEvent({
      type: AuditEventType.ProjectOpen,
      projectId: p.id,
    });
    await getRecentProjects();
    return p;
  };

  /**
   * Create a new project
   * @param projectDto  The project to create
   */
  const createProject = async (projectDto: ProjectDto): Promise<Project> => {
    const p = await ProjectService.createProject(projectDto);
    await getRecentProjects();
    return p;
  };

  /**
   * Load the model for the current project
   */
  const loadProjectModel = async () => {
    if (project.value) {
      project.value = await getProject(project.value.id);
    }
  };

  /**
   * Load the property descriptors for the current project
   * @param projectId The ID of the project to load properties for
   * If not provided, uses the current project's ID
   * @returns Promise that resolves when properties are loaded
   */
  async function loadProjectProperties(
    projectId: Project['id'] | undefined = project.value?.id,
  ) {
    if (!projectId) return; // No project to load properties for

    propertyDescriptors.value =
      await PanteraAPI.getExistingPropertyDescriptors(projectId);
    allPropertyDescriptors.value =
      await PanteraAPI.getPropertyDescriptors(projectId);
  }

  /**
   * Register a callback to be executed when project loading finishes
   * @param callback Function to execute when project loading is complete
   */
  const onProjectLoadFinish = (callback: () => void) => {
    // If project is already loaded, execute callback immediately
    if (project.value) {
      callback();
      return;
    }

    // Otherwise, add to callbacks array
    projectLoadFinishCallbacks.value.push(callback);
  };

  /**
   * Register a callback to be executed when switching between projects
   * @param callback Function to execute when project changes
   */
  const onProjectChange = (callback: () => void) => {
    projectChangeCallbacks.value.push(callback);
  };

  /**
   * Check if the project has existing data
   * @returns True if the project has existing data, false otherwise
   */
  const hasExistingData = computed<Boolean>(
    () => propertyDescriptors.value && propertyDescriptors.value.length > 0,
  );

  /**
   * Get the property descriptors for the current project containing the provided PropertyTag
   * @param propertyTag The PropertyTag to filter by
   * @returns : PropertyDescriptor list containing the provided PropertyTag
   */
  function getPropertyDescriptorsWithTag(
    propertyTag: PropertyTag,
  ): PropertyDescriptor[] {
    return (
      allPropertyDescriptors.value?.filter(descriptor =>
        descriptor?.tags?.includes(propertyTag),
      ) ?? []
    );
  }

  /**
   * Get the document IDs for the current project
   * This is used for navigation purposes, e.g., to load documents by ID
   * @returns Computed array of document IDs for the current project
   */
  const documentIds = computed(() => projectDocumentIds.value);

  // Get a list of all project routes to watch for changes
  const projectChildRouteNames = router
    .getRoutes()
    .filter(r => r.path.includes('project'))
    .flatMap(r => r.children.map(child => child.name));

  watch(
    () => route.params?.id?.toString(),
    async (newProjectId, oldProjectId, onInvalidate) => {
      let isCancelled = false;
      onInvalidate(() => {
        isCancelled = true;
      });

      // If the route is a project route, load the project if it's different from the current one
      if (
        route.name &&
        projectChildRouteNames.includes(route.name) &&
        project.value?.id !== newProjectId
      ) {
        project.value = undefined;
        projectDocumentIds.value = []; // Clear document IDs when project changes

        const p = await getProject(newProjectId);
        const permissionLevel = await ProjectService.getPermissionLevel(p.id);
        try {
          if (!isCancelled) {
            await loadProjectProperties(p.id);
          }
        } catch (e) {
          /*
          We likely tried to fetch the property descriptors before an index
          was created for this project as the last step of the ingest pipeline
          */
        } finally {
          const isAdmin = hasRole(RoleType.Admin);
          if (!isCancelled) {
            project.value = p;
            projectPermissionLevel.value = isAdmin
              ? ProjectPermissionLevel.Owner
              : permissionLevel;

            if (oldProjectId && oldProjectId !== newProjectId) {
              projectChangeCallbacks.value.forEach(callback => callback());
            }
            // Execute all registered callbacks
            projectLoadFinishCallbacks.value.forEach(callback => callback());
            projectLoadFinishCallbacks.value = [];
          }
        }
      } else {
        project.value = undefined;
        projectDocumentIds.value = []; // Clear document IDs when leaving project routes
      }
    },
    { immediate: true },
  );

  const getFileKey = async (file: File) => {
    const prefix = project?.value?.id ? `${project?.value?.id}/` : '';
    const hash = await hashFile(file);
    return `${prefix}${hash}_${file.name}`;
  };

  /**
   * Load documents for the current project and update documentIds
   * @param params LoadDataParams for pagination, sorting, filtering
   * @param cacheDocumentIds Whether to cache document IDs for navigation (default: false)
   */
  const loadDocuments = async (
    params?: LoadDataParams,
    cacheDocumentIds: boolean = false,
  ) => {
    if (!project.value) {
      return {
        items: [],
        documentCount: 0,
        isLoading: false,
        errorMessage: null,
      };
    }

    try {
      const response = await ProjectDataService.getRows(
        project.value.id,
        params,
      );

      // Extract document IDs from the response
      const items = response.content || [];

      // Cache document IDs if requested (for navigation)
      if (cacheDocumentIds) {
        projectDocumentIds.value = items.map(item => item.id);
        totalDocumentCount.value = response.totalElements;
      }

      return {
        items,
        documentCount: response.totalElements,
        isLoading: false,
        errorMessage: null,
      };
    } catch (e: any) {
      Logger.error('Error loading documents', e);
      return {
        items: [],
        documentCount: 0,
        isLoading: false,
        errorMessage: e?.response?.data?.message ?? 'Error loading documents',
      };
    }
  };

  /**
   * Load all document IDs for navigation purposes
   */
  const loadDocumentIds = async () => {
    if (!project.value) {
      projectDocumentIds.value = [];
      return;
    }

    try {
      // Load all documents to get their IDs
      const response = await ProjectDataService.getRows(project.value.id, {
        rows: 10000, // Load a large number to get all documents
      });

      const items = response.content || [];
      projectDocumentIds.value = items.map(item => item.id);
    } catch (e: any) {
      Logger.error('Error loading document IDs', e);
      projectDocumentIds.value = [];
    }
  };

  // Initialization
  getRecentProjects();

  return {
    getRecentProjects,
    hasExistingData,
    propertyDescriptors,
    allPropertyDescriptors,
    getPropertyDescriptorsWithTag,
    project,
    loadProjectModel,
    loadProjectProperties,
    projectPermissionLevel,
    recentProjects,
    createProject,
    getFileKey,
    loadDocuments,
    loadDocumentIds,
    documentIds,
    onProjectLoadFinish,
    onProjectChange,
    totalDocumentCount,
  };
});
export default useProjectStore;

