{
  "template" : {
    "settings" : {
      "index" : {
        "analysis" : {
          "filter" : {
            "english_stemmer" : {
              "type" : "stemmer",
              "language" : "english"
            },
            "english_stop" : {
              "type" : "stop",
              "stopwords" : "_english_"
            },
            "english_possessive_stemmer" : {
              "type" : "stemmer",
              "language" : "possessive_english"
            }
          },
          "char_filter" : {
            "exclude_url_portandsuffix" : {
              "pattern" : "(:|/).*",
              "type" : "pattern_replace",
              "replacement" : ""
            },
            "exclude_url_prefix" : {
              "flags" : "CASE_INSENSITIVE",
              "pattern" : "((https?)|(ftp))://",
              "type" : "pattern_replace",
              "replacement" : ""
            },
            "wordchars_only" : {
              "pattern" : "(\\W)",
              "type" : "pattern_replace",
              "replacement" : ""
            },
            "digits_only" : {
              "pattern" : "(\\D)",
              "type" : "pattern_replace",
              "replacement" : ""
            },
            "exclude_subdomain_from_hostname" : {
              "pattern" : "^([\\w\\-]+)\\.(?=([\\w\\-]+)\\.([\\w\\-]+))",
              "type" : "pattern_replace",
              "replacement" : ""
            }
          },
          "normalizer" : {
            "hostname_normalizer" : {
              "type" : "custom",
              "char_filter" : [ "exclude_url_prefix", "exclude_url_portandsuffix" ]
            },
            "domain_normalizer" : {
              "type" : "custom",
              "char_filter" : [ "exclude_url_prefix", "exclude_url_portandsuffix", "exclude_subdomain_from_hostname" ]
            },
            "lowercase_normalizer" : {
              "filter" : [ "lowercase" ],
              "type" : "custom"
            },
            "exact_phone_normalizer" : {
              "type" : "custom",
              "char_filter" : [ "digits_only" ]
            }
          },
          "analyzer" : {
            "sift_english_analyzer_v2" : {
              "filter" : [ "english_possessive_stemmer", "lowercase", "word_delimiter", "english_stop", "english_stemmer" ],
              "char_filter" : [ "html_strip" ],
              "type" : "custom",
              "tokenizer" : "icu_tokenizer"
            }
          }
        }
      }
    },
    "mappings" : {
      "dynamic_templates" : [ {
        "analyzed_string_features" : {
          "path_match" : "analyzed_feature.*",
          "mapping" : {
            "analyzer" : "sift_english_analyzer_v2",
            "type" : "text",
            "fields" : {
              "exact" : {
                "ignore_above" : 512,
                "type" : "keyword"
              },
              "language_en" : {
                "analyzer" : "sift_english_analyzer_v2",
                "type" : "text"
              }
            }
          },
          "match_mapping_type" : "string"
        }
      } ]
    }
  }
}
