+const nbResults = computed(() => {
+  const count = props.documentCount ?? 0;
+  if (count === 0) return 'No results';
+  if (count === 1) return '1 result';
+  if (count >= 10000) return `${formatCompactNumber(count)} results`;
+  return `${count} results`;
+});
+
+const locationsResults = computed(() => {
+  const count = props.locationCount ?? 0;
+  if (count === 0) return '';
+  if (count === 1) return 'across 1 location';
+  return `across ${count} locations`;
+});
+
+const nbResultsIfFiltersApplied = computed(() => {
+  if (isNil(props.filteredDocumentCount)) return '';
+  if (props.filteredDocumentCount === props.documentCount) return '';
+  const count = props.filteredDocumentCount ?? 0;
+  if (count === 0) return 'No results if filters applied';
+  if (count === 1) return '1 result if filters applied';
+  if (count >= 10000)
+    return `${formatCompactNumber(count)} results if filters applied`;
+  return `${count} results if filters applied`;
+});
