+import Logger from '@/services/Logger';
+import PanteraAPI from '@/services/PanteraAPI';
+
+/**
+ * Wait for a specified duration
+ * @param ms Milliseconds to wait
+ */
+function delay(ms: number): Promise<void> {
+  return new Promise(resolve => {
+    setTimeout(resolve, ms);
+  });
+}
+
+/**
+ * Check task status and poll recursively until completion
+ */
+function checkTaskStatus(
+  nodeId: string,
+  taskNumber: number,
+  taskId: string,
+  startTime: number,
+  pollInterval: number,
+  timeout: number,
+): Promise<void> {
+  if (Date.now() - startTime >= timeout) {
+    Logger.warn(`Task ${taskId} polling timed out after ${timeout}ms`);
+    return Promise.resolve();
+  }
+
+  return PanteraAPI.getTask(nodeId, taskNumber)
+    .then(response => {
+      if (response.completed) {
+        Logger.info(`Task ${taskId} completed successfully`);
+        return Promise.resolve();
+      }
+
+      // Wait before next poll, then check again
+      return delay(pollInterval).then(() =>
+        checkTaskStatus(
+          nodeId,
+          taskNumber,
+          taskId,
+          startTime,
+          pollInterval,
+          timeout,
+        ),
+      );
+    })
+    .catch(error => {
+      Logger.error(`Error polling task ${taskId}:`, error);
+      // Continue polling despite errors as task might still be running
+      return delay(pollInterval).then(() =>
+        checkTaskStatus(
+          nodeId,
+          taskNumber,
+          taskId,
+          startTime,
+          pollInterval,
+          timeout,
+        ),
+      );
+    });
+}
+
+/**
+ * Poll an Elasticsearch task until completion
+ * @param taskId The task ID in format "nodeId:taskId"
+ * @param pollInterval Interval in milliseconds between polls (default: 500ms)
+ * @param timeout Maximum time to wait in milliseconds (default: 60000ms / 1 minute)
+ * @returns Promise that resolves when task is complete
+ */
+export default function pollTaskCompletion(
+  taskId: string,
+  pollInterval = 500,
+  timeout = 60000,
+): Promise<void> {
+  const startTime = Date.now();
+  const [nodeId, taskNumber] = taskId.split(':');
+
+  if (!nodeId || !taskNumber) {
+    Logger.error(`Invalid task ID format: ${taskId}`);
+    return Promise.resolve();
+  }
+
+  return checkTaskStatus(
+    nodeId,
+    parseInt(taskNumber, 10),
+    taskId,
+    startTime,
+    pollInterval,
+    timeout,
+  );
+}
