   239â†’      [facetKey]: newMap,
   240â†’    };
   241â†’  };
   242â†’
   243â†’  /**
   244â†’   * Updates whether the "top" or "bottom" buckets, and how many of them, should be displayed.
   245â†’   * @param facetKey The facet whose sort state should be updated.
   246â†’   * @param sortDirection The updated sort direction.
   247â†’   * @param visibleRowCount The number of buckets that should now be visible.
   248â†’   * @param includes
   249â†’   */
   250â†’  const setCategoricalFacetSortState = (
   251â†’    facetKey: string,
   252â†’    sortDirection: number,
   253â†’    visibleRowCount: number,
   254â†’    includes?: string[],
   255â†’  ) => {
   256â†’    const displayState = getCategoricalFacetDisplayState(facetKey);
   257â†’    const selectedBucketKeys = getCategoricalFacetBuckets(facetKey);
   258â†’
   259â†’    const newMap = {
   260â†’      ...displayState,
   261â†’      selectedBucketKeys,
   262â†’      includes: includes ?? [],
   263â†’    };
   264â†’    newMap.sortDirection = sortDirection;
   265â†’    newMap.visibleRowCount = visibleRowCount;
   266â†’    categoricalFacetState.value = {
   267â†’      ...categoricalFacetState.value,
   268â†’      [facetKey]: newMap,
   269â†’    };
   270â†’  };
   271â†’
   272â†’  /**
   273â†’   * Updates the selected interval for a given numeric or date facet.
   274â†’   * @param facetKey The unique identifier of the numeric or date facet
   275â†’   * @param selectedInterval The selected min and max, or null if the selection is being cleared
   276â†’   */
   277â†’  const setNumericFacetSelectionState = (
   278â†’    facetKey: string,
   279â†’    selectedInterval: SelectionInterval | null,
   280â†’  ) => {
   281â†’    if (selectedInterval === null) {
   282â†’      const newState = {
   283â†’        ...pendingNumericFacets.value,
   284â†’      };
   285â†’      delete newState[facetKey];
   286â†’      pendingNumericFacets.value = newState;
   287â†’      return;
   288â†’    }
   289â†’    pendingNumericFacets.value = {
   290â†’      ...pendingNumericFacets.value,
   291â†’      [facetKey]: selectedInterval,
   292â†’    };
   293â†’  };
   294â†’  const clearAllNumericFacetSelections = () => {
   295â†’    pendingNumericFacets.value = {};
