   311â†’  };
   312â†’
   313â†’  const projectStore = useProjectStore();
   314â†’
   315â†’  const facetData = ref<FacetsResult | undefined>();
   316â†’
   317â†’  // Private helper function
   318â†’  function buildDisplayState(): {
   319â†’    [facetKey: string]: CategoricalFacetDisplayState;
   320â†’  } {
   321â†’    const displayState: { [facetKey: string]: CategoricalFacetDisplayState } =
   322â†’      {};
