   324â†’      ([
   325â†’        facetKey,
   326â†’        { visibleRowCount, sortDirection, queryString, includes },
   327â†’      ]) => {
   328â†’        // If includes is non-empty, use it. Otherwise, use the currently selected buckets to make sure selected terms are included in the results.
   329â†’        const selectedValues =
   330â†’          categoricalFacetState.value[facetKey]?.selectedBucketKeys;
   331â†’        displayState[facetKey] = {
   332â†’          sortDirection,
   333â†’          visibleRowCount,
   334â†’          queryString,
   335â†’          includes: !_.isEmpty(includes) ? includes : (selectedValues ?? []),
   336â†’        };
   337â†’      },
   338â†’    );
   339â†’    return displayState;
   340â†’  }
   341â†’
   342â†’  function clearIncludesFromDisplayState() {
   343â†’    const displayState: { [facetKey: string]: CategoricalFacetDisplayState } =
   344â†’      {};
   345â†’    Object.entries(categoricalFacetState.value).forEach(
   346â†’      ([facetKey, { visibleRowCount, sortDirection, queryString }]) => {
   347â†’        displayState[facetKey] = {
   348â†’          sortDirection,
   349â†’          visibleRowCount,
   350â†’          queryString,
   351â†’          includes: [],
   352â†’        };
   353â†’      },
   354â†’    );
   355â†’  }
   356â†’
   357â†’  /**
   358â†’   * Builds the facet selection state from the pending categorical and numeric facets.
   359â†’   * This is used to convert the pending selections into a format that can be sent to the backend.
   360â†’   * @param pendingCategorical The pending categorical selections
   361â†’   * @param pendingNumeric The pending numeric selections
   362â†’   * @returns An object containing the categorical and numeric facet states, as well as the lucene query
   363â†’   */
   364â†’  function buildFacetSelectionState(
   365â†’    pendingCategorical = pendingCategoricalFacets.value,
   366â†’    pendingNumeric = pendingNumericFacets.value,
   367â†’  ): {
   368â†’    categorical: CategoricalFacetState;
   369â†’    numeric: NumericFacetSelectionState;
   370â†’    luceneQuery: string;
   371â†’  } {
   372â†’    const categorical: CategoricalFacetState = {};
   373â†’
   374â†’    Object.entries(pendingCategorical).forEach(
   375â†’      ([facetKey, selectedBucketKeys]) => {
   376â†’        const displayState = getCategoricalFacetDisplayState(facetKey);
   377â†’        categorical[facetKey] = {
   378â†’          ...displayState,
   379â†’          selectedBucketKeys,
   380â†’        };
   381â†’      },
   382â†’    );
   383â†’
   384â†’    const numeric = { ...pendingNumeric };
   385â†’
   386â†’    return {
   387â†’      categorical,
   388â†’      numeric,
   389â†’      luceneQuery: FacetsUtil.convertFacetSelectionStateToLuceneQuery(
   390â†’        categorical,
   391â†’        numeric,
   392â†’      ),
   393â†’    };
   394â†’  }
   395â†’
   396â†’  // Apply pending categorical selections to the categorical facet state
   397â†’  const applyFacetSelections = () => {
   398â†’    const { categorical, numeric, luceneQuery } = buildFacetSelectionState();
   399â†’
   400â†’    categoricalFacetState.value = categorical;
   401â†’    numericFacetSelectionState.value = numeric;
   402â†’    facetSelectionLuceneQuery.value = luceneQuery;
   403â†’  };
   404â†’
   405â†’  // Return the amount of rows a user would get should they apply the pending facets.
   406â†’  // Note that this will not impact the state variables
   407â†’  const getPendingFacetResultCount = async () => {
   408â†’    if (
   409â†’      projectStore.project === undefined ||
   410â†’      visibleFacetKeys.value.length === 0
   411â†’    ) {
   412â†’      facetData.value = undefined;
   413â†’      return 0;
   414â†’    }
   415â†’
   416â†’    const { luceneQuery } = buildFacetSelectionState();
   417â†’    const displayState = buildDisplayState();
   418â†’
   419â†’    // Call the API
   420â†’    const apiResult = await ProjectDataService.getFacetData(
   421â†’      projectStore.project.id,
   422â†’      visibleFacetKeys.value,
   423â†’      {
   424â†’        queryString: textQuery.value,
   425â†’        filter: FacetsUtil.concatLuceneQueries(
   426â†’          luceneQuery,
   427â†’          additionalLuceneQuery.value,
   428â†’        ),
   429â†’        displayState,
   430â†’      },
   431â†’    );
   432â†’
   433â†’    return apiResult.count;
   434â†’  };
   435â†’
   436â†’  async function refreshFacetData() {
   437â†’    if (
   438â†’      projectStore.project === undefined ||
   439â†’      visibleFacetKeys.value.length === 0
   440â†’    ) {
   441â†’      facetData.value = undefined;
   442â†’      return;
   443â†’    }
   444â†’
   445â†’    const displayState = buildDisplayState();
   446â†’
   447â†’    // Call the API
   448â†’    const apiResult = await ProjectDataService.getFacetData(
   449â†’      projectStore.project.id,
   450â†’      visibleFacetKeys.value,
   451â†’      {
   452â†’        queryString: textQuery.value,
   453â†’        filter: FacetsUtil.concatLuceneQueries(
   454â†’          facetSelectionLuceneQuery.value,
   455â†’          additionalLuceneQuery.value,
   456â†’        ),
   457â†’        displayState,
   458â†’      },
   459â†’    );
   460â†’    clearIncludesFromDisplayState();
   461â†’
   462â†’    facetData.value = {
   463â†’      count: Object.keys(apiResult.facets).length,
   464â†’      facets: {
   465â†’        ...apiResult.facets,
   466â†’      },
   467â†’      maxUniqueValueCount: apiResult.maxUniqueValueCount,
   468â†’    };
   469â†’  }
   470â†’
   471â†’  // When the combined query state (facet selections + search bar text) changes or the sort/limit
   472â†’  //  state within a facet changes, fetch updated facet data.
   473â†’  watch(
   474â†’    [
   475â†’      () => projectStore.project,
   476â†’      visibleFacetKeys,
   477â†’      facetSelectionLuceneQuery,
   478â†’      textQuery,
   479â†’      categoricalFacetState,
   480â†’    ],
   481â†’    refreshFacetData,
   482â†’  );
   483â†’
   484â†’  const clearSelectionForFacet = (facetKey: string, facetType: FacetType) => {
   485â†’    if (facetType === FacetType.Categorical) {
   486â†’      clearSelectedRowsForFacet(facetKey);
   487â†’    } else if (facetType === FacetType.Numeric) {
   488â†’      setNumericFacetSelectionState(facetKey, null);
   489â†’    } else {
   490â†’      Logger.error(
   491â†’        `Attempting to clear selection for facet ${facetKey} with unsupported type ${facetType}.`,
   492â†’      );
   493â†’    }
   494â†’    applyFacetSelections();
   495â†’  };
   496â†’
   497â†’  const clearAllSelections = () => {
   498â†’    clearAllCategoricalFacetSelections();
   499â†’    clearAllNumericFacetSelections();
   500â†’    applyFacetSelections();
   501â†’  };
   502â†’
   503â†’  // Delete all facet selections (both pending and applied)
   504â†’  const deleteFacetSelections = async () => {
   505â†’    const projectId = projectStore.project?.id;
   506â†’    if (projectId === undefined) {
   507â†’      Logger.error(
   508â†’        'Attempted to delete facet selections when no project is loaded.',
   509â†’      );
   510â†’      return;
   511â†’    }
   512â†’    const { documentIds } = projectStore;
   513â†’    if (documentIds.length === 0) {
   514â†’      Logger.error(
   515â†’        'Attempted to delete facet selections when no documents are loaded.',
   516â†’      );
   517â†’      return;
   518â†’    }
   519â†’
   520â†’    // Build the deletion criteria from both pending and applied facets
   521â†’    const deletionCriteria: PropertyValueDeletionCriteria[] = [];
