   523â†’
   524â†’    // Categorical facets
   525â†’    Object.entries(categorical)
   526â†’      .filter(
   527â†’        ([propertyKey]) =>
   528â†’          propertyKey !== 'set' && propertyKey !== 'provenance_source',
   529â†’      )
   530â†’      .forEach(property => {
   531â†’        const [propertyKey, { selectedBucketKeys }] = property;
   532â†’        selectedBucketKeys
   533â†’          .filter(value => value !== 'N/A')
   534â†’          .forEach(exactValue =>
   535â†’            deletionCriteria.push({ propertyKey, exactValue }),
   536â†’          );
   537â†’      });
   538â†’
   539â†’    // Numeric facets
   540â†’    Object.entries(numeric)
   541â†’      .filter(
   542â†’        ([propertyKey]) =>
   543â†’          propertyKey !== 'set' && propertyKey !== 'provenance_source',
   544â†’      )
   545â†’      .forEach(property => {
   546â†’        const [propertyKey, { intervalMin, intervalMax }] = property;
   547â†’        deletionCriteria.push({
   548â†’          propertyKey,
   549â†’          minValue: intervalMin.toString(),
   550â†’          maxValue: intervalMax.toString(),
   551â†’        });
   552â†’      });
   553â†’
   554â†’    if (deletionCriteria.length === 0) {
   555â†’      Logger.error('No facet selections to delete.');
   556â†’      return;
   557â†’    }
   558â†’
   559â†’    const request: BulkPropertyValueDeletionRequest = {
   560â†’      documentIds,
   561â†’      deletionCriteria,
   562â†’    };
   563â†’
   564â†’    try {
   565â†’      isDeletingFacetSelections.value = true;
   566â†’
   567â†’      // Call the API to remove all properties values selected
   568â†’      const response = await PanteraAPI.bulkRemovePropertyValues(
   569â†’        projectId,
   570â†’        request,
   571â†’      );
   572â†’
   573â†’      Logger.info(
   574â†’        `Started bulk property removal with task ID: ${response.taskId}`,
   575â†’      );
   576â†’
   577â†’      // Wait for the Elasticsearch task to complete before refreshing facet data
   578â†’      await pollTaskCompletion(response.taskId);
   579â†’
   580â†’      // Clear all selections after successful deletion
   581â†’      clearAllSelections();
   582â†’
   583â†’      refreshFacetData();
   584â†’    } finally {
   585â†’      isDeletingFacetSelections.value = false;
   586â†’    }
   587â†’  };
   588â†’
   589â†’  const hasPendingChanges = computed(
   590â†’    () =>
   591â†’      Object.entries(pendingCategoricalFacets.value).some(
   592â†’        ([facetKey, selectedBucketKeys]) =>
   593â†’          !_.isEqual(
   594â†’            selectedBucketKeys,
   595â†’            categoricalFacetState.value[facetKey]?.selectedBucketKeys ?? [],
   596â†’          ),
   597â†’      ) ||
   598â†’      Object.entries(pendingNumericFacets.value).some(
   599â†’        ([facetKey, selectedInterval]) =>
   600â†’          !_.isEqual(
   601â†’            selectedInterval,
   602â†’            numericFacetSelectionState.value[facetKey] ?? null,
   603â†’          ),
   604â†’      ),
   605â†’  );
   606â†’
   607â†’  /**
   608â†’   * Checks whether there are any selected facets, either pending or applied.
   609â†’   */
   610â†’  const hasSelectedFacets = computed(
   611â†’    () =>
   612â†’      Object.values(pendingCategoricalFacets.value).some(
   613â†’        values => values.length > 0,
   614â†’      ) ||
   615â†’      Object.keys(pendingNumericFacets.value).length > 0 ||
   616â†’      Object.values(categoricalFacetState.value).some(
   617â†’        state => state.selectedBucketKeys.length > 0,
   618â†’      ) ||
   619â†’      Object.keys(numericFacetSelectionState.value).length > 0,
   620â†’  );
   621â†’
   622â†’  const numPendingChanges = computed(
   623â†’    () =>
   624â†’      Object.values(pendingCategoricalFacets.value).reduce(
   625â†’        (sum, selectedBucketKeys) => sum + selectedBucketKeys.length,
   626â†’        0,
   627â†’      ) + Object.keys(pendingNumericFacets.value).length,
   628â†’  );
   629â†’
   630â†’  /**
   631â†’   * An array of objects with enough information to summarize the facet selection state.
   632â†’   * Used to render the chips at the top of the facet panel.
   633â†’   */
   634â†’  const facetSelectionsList = computed<FacetSelectionListItem[]>(() => {
