   636â†’      facetData.value?.facets ?? {},
   637â†’    )
   638â†’      .filter(
   639â†’        ([, data]) =>
   640â†’          data.properties.length > 0 &&
   641â†’          data.properties[0].tags.includes(PropertyTag.Date),
   642â†’      )
