   644â†’    return FacetsUtil.convertFacetSelectionStateToList(
   645â†’      categoricalFacetState.value,
   646â†’      numericFacetSelectionState.value,
   647â†’      facetsThatRequireDateFormatting,
   648â†’      facetMetadata.value,
   649â†’    );
   650â†’  });
   651â†’
   652â†’  return {
   653â†’    setIsFacetExpanded,
   654â†’    isFacetExpanded,
   655â†’    facetData,
