  @PrefixedCacheEvict
  public String bulkRemovePropertyValuesFromDocuments(
    final Project project,
    final List<PropertyValueDeletionCriteria> deletionCriteria,
    final List<String> documentIds
  ) {
    if (deletionCriteria == null || deletionCriteria.isEmpty()) {
      throw new IllegalArgumentException(
        "Deletion criteria list cannot be null or empty"
      );
    }
    if (documentIds == null || documentIds.isEmpty()) {
      throw new IllegalArgumentException(
        "Document IDs list cannot be null or empty"
      );
    }
    log.info(
      "Starting bulk property value removal for project {} with {} criteria in {} documents",
      project.getId(),
      deletionCriteria.size(),
      documentIds.size()
    );
    try {
      // Validate that all property descriptors exist and criteria are valid
      for (final PropertyValueDeletionCriteria criteria : deletionCriteria) {
        if (
          criteria.getPropertyKey() == null ||
          criteria.getPropertyKey().isEmpty()
        ) {
          throw new IllegalArgumentException(
            "Property key cannot be null or empty in deletion criteria"
          );
        }
        // Validate that either exactValue exists OR both minValue and maxValue exist
        final boolean hasExactValue =
          criteria.getExactValue() != null &&
          !criteria.getExactValue().isEmpty();
        final boolean hasMinValue =
          criteria.getMinValue() != null && !criteria.getMinValue().isEmpty();
--
          );
        }
      }
      // Execute bulk value removal via Elasticsearch
      final String taskId = elasticsearchService.bulkRemovePropertyValues(
        project.getIndexName(),
        deletionCriteria,
        documentIds
      );
      log.info(
        "Initiated bulk property value removal for project {} with task ID: {}",
        project.getId(),
        taskId
      );
      return taskId;
    } catch (final Exception e) {
      log.error(
        "Failed to execute bulk property value removal for project {}: {}",
        project.getId(),
        e.getMessage(),
        e
      );
      throw new RuntimeException(
        "Failed to execute bulk property value removal",
        e
      );
    }
  }
}

