import FacetsUtil, {
  CategoricalFacetState,
  FacetSelectionListItem,
  FacetType,
  NumericFacetSelectionState,
} from '@/util/FacetsUtil';
import { describe, expect, it } from 'vitest';

describe('FacetsUtil', () => {
  describe('convertCategoricalFacetsToLuceneQueryTerms', () => {
    it('returns an empty array when the facet state contains no bucket keys', () => {
      const state: CategoricalFacetState = {};
      const result =
        FacetsUtil.convertCategoricalFacetsToLuceneQueryTerms(state);
      expect(result.length).toBe(0);
    });
    it('returns an empty array when the facet state contains facet keys with empty selected bucket arrays', () => {
      const state: CategoricalFacetState = {
        myFacetKey: {
          selectedBucketKeys: [],
          queryString: '',
          sortDirection: -1,
          visibleRowCount: 5,
          includes: [],
        },
        mySecondFacetKey: {
          selectedBucketKeys: [],
          queryString: 'test',
          sortDirection: 1,
          visibleRowCount: 25,
          includes: [],
        },
      };
      const result =
        FacetsUtil.convertCategoricalFacetsToLuceneQueryTerms(state);
      expect(result.length).toBe(0);
    });
    it('correctly handles one selected bucket and wraps bucket keys in quotes', () => {
      const state: CategoricalFacetState = {
        myFacetKey: {
          selectedBucketKeys: ['myBucketKey1'],
          queryString: '',
          sortDirection: -1,
          visibleRowCount: 5,
          includes: [],
        },
      };
      const result =
        FacetsUtil.convertCategoricalFacetsToLuceneQueryTerms(state);
      expect(result).to.deep.equal(['myFacetKey:"myBucketKey1"']);

