// Delete all facet selections (both pending and applied)
const deleteFacetSelections = async () => {
  const projectId = projectStore.project?.id;
  if (projectId === undefined) {
    Logger.error(
      'Attempted to delete facet selections when no project is loaded.',
    );
    return;
  }
  const { documentIds } = projectStore;
  if (documentIds.length === 0) {
    Logger.error(
      'Attempted to delete facet selections when no documents are loaded.',
    );
    return;
  }

  // Build the deletion criteria from both pending and applied facets
  const deletionCriteria: PropertyValueDeletionCriteria[] = [];
  const { categorical, numeric } = buildFacetSelectionState();

  // Categorical facets
  Object.entries(categorical)
    .filter(
      ([propertyKey]) =>
        propertyKey !== 'set' && propertyKey !== 'provenance_source',
    )
    .forEach(property => {
      const [propertyKey, { selectedBucketKeys }] = property;
      selectedBucketKeys
        .filter(value => value !== 'N/A')
        .forEach(exactValue =>
          deletionCriteria.push({ propertyKey, exactValue }),
        );
    });

  // Numeric facets
  Object.entries(numeric)
    .filter(
      ([propertyKey]) =>
        propertyKey !== 'set' && propertyKey !== 'provenance_source',
    )
    .forEach(property => {
      const [propertyKey, { intervalMin, intervalMax }] = property;
      deletionCriteria.push({
        propertyKey,
        minValue: intervalMin.toString(),
        maxValue: intervalMax.toString(),
      });
    });

  if (deletionCriteria.length === 0) {
    Logger.error('No facet selections to delete.');
    return;
  }

  const request: BulkPropertyValueDeletionRequest = {
    documentIds,
    deletionCriteria,
  };

  try {
    isDeletingFacetSelections.value = true;

    // Call the API to remove all properties values selected
    const response = await PanteraAPI.bulkRemovePropertyValues(
      projectId,
      request,
    );

    Logger.info(
      `Started bulk property removal with task ID: ${response.taskId}`,
    );

    // Wait for the Elasticsearch task to complete before refreshing facet data
    await pollTaskCompletion(response.taskId);

    // Clear all selections after successful deletion
    clearAllSelections();

    refreshFacetData();
  } finally {
    isDeletingFacetSelections.value = false;
  }
};

