import Logger from '@/services/Logger';
import ProjectDataService from '@/services/ProjectDataService';
import useProjectStore from '@/stores/ProjectStore';
import {
  AggregateProperties,
  BulkPropertyValueDeletionRequest,
  CategoricalFacetDisplayState,
  FacetsResult,
  PropertyTag,
  PropertyValueDeletionCriteria,
  SelectionInterval,
} from '@/types/Types';
import FacetsUtil, {
  CategoricalFacetState,
  DEFAULT_SORT_DIRECTION,
  DEFAULT_VISIBLE_ROW_COUNT,
  FacetMetadata,
  FacetSelectionListItem,
  FacetType,
  NumericFacetSelectionState,
} from '@/util/FacetsUtil';
import _ from 'lodash';
import { Ref, computed, ref, watch } from 'vue';
import PanteraAPI from '@/services/PanteraAPI';
import pollTaskCompletion from '@/util/Tasks';

// Global state:
const categoricalFacetState = ref<CategoricalFacetState>({});
const pendingNumericFacets = ref<NumericFacetSelectionState>({});
const facetSelectionLuceneQuery = ref('');
/**
 * Extra query that are not part of the facet selections, e.g. location filters
 */
const additionalLuceneQuery = ref('');

export interface Facets {
  setIsFacetExpanded: (facetId: string, newValue: boolean) => void;
  isFacetExpanded: (facetId: string) => boolean;
  facetData: Ref<FacetsResult | undefined>;
  categoricalFacetState: Ref<CategoricalFacetState>;
  getCategoricalFacetDisplayState: (
    facetKey: string,
  ) => CategoricalFacetDisplayState;
  getCategoricalFacetBuckets: (facetKey: string) => string[];
  // Stores the pending selections for categorical and numeric facets
  pendingCategoricalFacets: Ref<{ [facetKey: string]: string[] }>;
  pendingNumericFacets: Ref<NumericFacetSelectionState>;
  setCategoricalFacetQueryState: (
    facetKey: string,
    queryString: string,
  ) => void;
  setCategoricalFacetSortState: (
    facetKey: string,
    sortDirection: number,
    visibleRowCount: number,
    includes?: string[],
  ) => void;
  setIsRowSelected: (
    facetKey: string,
    bucketKey: string,
    isSelected: boolean,
  ) => void;
  numericFacetSelectionState: Ref<NumericFacetSelectionState>;
  setNumericFacetSelectionState: (
    facetKey: string,
    selectedInterval: SelectionInterval | null,
  ) => void;
  setAdditionalFilter: (query: string) => void;
  isHistogramFacet: (aggregateProperties: AggregateProperties) => boolean;
  facetSelectionLuceneQuery: Ref<string>;
  facetSelectionsList: Ref<FacetSelectionListItem[]>;
  clearSelectionForFacet: (facetKey: string, facetType: FacetType) => void;
  clearAllSelections: () => void;
  visibleFacetKeys: Ref<string[]>;
  setVisibleFacetKeys: (facetKeys: string[]) => void;
  setCategoricalFacetState: (facetState: CategoricalFacetState) => void;
  setPendingCategoricalFacets: (facetKey: string, bucketKeys: string[]) => void;
  setNumericFacetState: (facetState: NumericFacetSelectionState) => void;
  refreshFacetData: () => void;
  applyFacetSelections: () => void;
  deleteFacetSelections: () => void;
  hasPendingChanges: Ref<boolean>;
  numPendingChanges: Ref<number>;
  hasSelectedFacets: Ref<boolean>;
  getPendingFacetResultCount: () => Promise<number>;
  isDeletingFacetSelections: Ref<boolean>;
}

export default function useFacets(
  textQuery: Ref<string>,
  facetMetadata: Ref<{ [facetKey: string]: FacetMetadata }>,
): Facets {
  const pendingCategoricalFacets = ref<{ [facetKey: string]: string[] }>({});
  const isDeletingFacetSelections = ref(false);

  const collapsedFacetIds = ref<{ [facetId: string]: boolean }>({});
  const setIsFacetExpanded = (facetId: string, newValue: boolean) => {
    collapsedFacetIds.value[facetId] = !newValue;
  };
  const isFacetExpanded = (facetId: string) =>
    !collapsedFacetIds.value[facetId];

  const visibleFacetKeys = ref<string[]>([]);
  const setVisibleFacetKeys = (facetKeys: string[]) => {
    visibleFacetKeys.value = facetKeys;
  };

  /**
   * Stores the selected buckets in each categorical facet, which are equivalent to the applied
   * filters.
   * Also includes the sort direction, number of visible rows, and bucket query within each facet.
   * If a facet is not found in this object, it has default state.
   */
  const setCategoricalFacetState = (facetState: CategoricalFacetState) => {
    pendingCategoricalFacets.value = Object.fromEntries(
      Object.entries(facetState).map(([facetKey, state]) => [
        facetKey,
        state.selectedBucketKeys || [],
      ]),
    );
    categoricalFacetState.value = facetState;
  };

  /**
   * Gets the state of a categorical facet.
   * @param facetKey The facet whose state should be returned.
   * @returns A shallow clone of the facet's state, or a new state object with default values if the facet's state hasn't yet been overridden.
   */
  const getCategoricalFacetDisplayState = (
    facetKey: string,
  ): CategoricalFacetDisplayState => ({
    sortDirection:
      categoricalFacetState.value[facetKey]?.sortDirection ??
      DEFAULT_SORT_DIRECTION,
    visibleRowCount:
      categoricalFacetState.value[facetKey]?.visibleRowCount ??
      DEFAULT_VISIBLE_ROW_COUNT,
    queryString: categoricalFacetState.value[facetKey]?.queryString ?? '',
    includes: categoricalFacetState.value[facetKey]?.includes ?? [],
  });

  const getCategoricalFacetBuckets = (facetKey: string): string[] =>
    categoricalFacetState.value[facetKey]?.selectedBucketKeys ?? [];

  const setPendingCategoricalFacets = (
    facetKey: string,
    bucketKeys: string[],
  ) => {
    if (bucketKeys.length === 0)
      delete pendingCategoricalFacets.value[facetKey];
    pendingCategoricalFacets.value[facetKey] = bucketKeys;
  };

  /**
   * Sets the selection state for one bucket within one categorical facet.
   * @param facetKey The unique identifier of the facet
   * @param bucketKey The unique identifier of the bucket within that facet
   * @param isSelected Whether that bucket is now selected or not
   */
  const setIsRowSelected = (
    facetKey: string,
    bucketKey: string,
    isSelected: boolean,
  ) => {
    const displayState = getCategoricalFacetDisplayState(facetKey);
    const selectedBucketKeys = getCategoricalFacetBuckets(facetKey);

    const newMap = {
      ...displayState,
      selectedBucketKeys,
    };
    const newSelectedBucketKeys = [
      ...(pendingCategoricalFacets.value[facetKey]?.filter(
        key => key !== bucketKey,
      ) || []),
    ];
    if (isSelected) {
      newSelectedBucketKeys.push(bucketKey);
    }
    newMap.selectedBucketKeys = newSelectedBucketKeys;
    pendingCategoricalFacets.value[facetKey] = newSelectedBucketKeys;
  };
  const clearSelectedRowsForFacet = (facetKey: string) => {
    const displayState = getCategoricalFacetDisplayState(facetKey);

    const newMap = {
      ...displayState,
      selectedBucketKeys: [],
    };
    pendingCategoricalFacets.value[facetKey] = [];
    categoricalFacetState.value = {
      ...categoricalFacetState.value,
      [facetKey]: newMap,
    };
  };
  const clearAllCategoricalFacetSelections = () => {
    pendingCategoricalFacets.value = {};
    const newCategoricalFacetState: CategoricalFacetState = {};
    Object.keys(categoricalFacetState.value).forEach(facetKey => {
      newCategoricalFacetState[facetKey] = {
        ...getCategoricalFacetDisplayState(facetKey),
        selectedBucketKeys: [],
      };
    });
    categoricalFacetState.value = newCategoricalFacetState;
  };

  /**
   * Stores the selected intervals for all numeric and date facets.
   * If a facet has no interval selected (which is interpreted as the whole range being selected)
   * then it will not have an entry in this map.
   */
  const numericFacetSelectionState = ref<NumericFacetSelectionState>({});
  const setNumericFacetState = (facetState: NumericFacetSelectionState) => {
    pendingNumericFacets.value = facetState;
    numericFacetSelectionState.value = facetState;
  };

  /**
   * Updates the search string within a given facet.
   * This query is used to filter the buckets that are displayed within that facet.
   * @param facetKey The facet whose query string should be updated.
   * @param queryString The updated query string.
   */
  const setCategoricalFacetQueryState = (
    facetKey: string,
    queryString: string,
  ) => {
    const displayState = getCategoricalFacetDisplayState(facetKey);
    const selectedBucketKeys = getCategoricalFacetBuckets(facetKey);

    const newMap = {
      ...displayState,
      selectedBucketKeys,
    };
    newMap.queryString = queryString;
    categoricalFacetState.value = {
      ...categoricalFacetState.value,
      [facetKey]: newMap,
    };
  };

  /**
   * Updates whether the "top" or "bottom" buckets, and how many of them, should be displayed.
   * @param facetKey The facet whose sort state should be updated.
   * @param sortDirection The updated sort direction.
   * @param visibleRowCount The number of buckets that should now be visible.
   * @param includes
   */
  const setCategoricalFacetSortState = (
    facetKey: string,
    sortDirection: number,
    visibleRowCount: number,
    includes?: string[],
  ) => {
    const displayState = getCategoricalFacetDisplayState(facetKey);
    const selectedBucketKeys = getCategoricalFacetBuckets(facetKey);

    const newMap = {
      ...displayState,
      selectedBucketKeys,
      includes: includes ?? [],
    };
    newMap.sortDirection = sortDirection;
    newMap.visibleRowCount = visibleRowCount;
    categoricalFacetState.value = {
      ...categoricalFacetState.value,
      [facetKey]: newMap,
    };
  };

  /**
   * Updates the selected interval for a given numeric or date facet.
   * @param facetKey The unique identifier of the numeric or date facet
   * @param selectedInterval The selected min and max, or null if the selection is being cleared
   */
  const setNumericFacetSelectionState = (
    facetKey: string,
    selectedInterval: SelectionInterval | null,
  ) => {
    if (selectedInterval === null) {
      const newState = {
        ...pendingNumericFacets.value,
      };
      delete newState[facetKey];
      pendingNumericFacets.value = newState;
      return;
    }
    pendingNumericFacets.value = {
      ...pendingNumericFacets.value,
      [facetKey]: selectedInterval,
    };
  };
  const clearAllNumericFacetSelections = () => {
    pendingNumericFacets.value = {};
    numericFacetSelectionState.value = {};
  };

  const setAdditionalFilter = (query: string) => {
    additionalLuceneQuery.value = query;
  };

  const isHistogramFacet = (aggregateProperties: AggregateProperties) => {
    const { properties } = aggregateProperties;
    return (
      properties.length > 0 &&
      aggregateProperties.uniqueValueCount >= 10 &&
      (properties[0].tags.includes(PropertyTag.Number) ||
        properties[0].tags.includes(PropertyTag.Date))
    );
  };

  const projectStore = useProjectStore();

  const facetData = ref<FacetsResult | undefined>();

  // Private helper function
  function buildDisplayState(): {
    [facetKey: string]: CategoricalFacetDisplayState;
  } {
    const displayState: { [facetKey: string]: CategoricalFacetDisplayState } =
      {};
    Object.entries(categoricalFacetState.value).forEach(
      ([
        facetKey,
        { visibleRowCount, sortDirection, queryString, includes },
      ]) => {
        // If includes is non-empty, use it. Otherwise, use the currently selected buckets to make sure selected terms are included in the results.
        const selectedValues =
          categoricalFacetState.value[facetKey]?.selectedBucketKeys;
        displayState[facetKey] = {
          sortDirection,
          visibleRowCount,
          queryString,
          includes: !_.isEmpty(includes) ? includes : (selectedValues ?? []),
        };
      },
    );
    return displayState;
  }

  function clearIncludesFromDisplayState() {
    const displayState: { [facetKey: string]: CategoricalFacetDisplayState } =
      {};
    Object.entries(categoricalFacetState.value).forEach(
      ([facetKey, { visibleRowCount, sortDirection, queryString }]) => {
        displayState[facetKey] = {
          sortDirection,
          visibleRowCount,
          queryString,
          includes: [],
        };
      },
    );
  }

  /**
   * Builds the facet selection state from the pending categorical and numeric facets.
   * This is used to convert the pending selections into a format that can be sent to the backend.
   * @param pendingCategorical The pending categorical selections
   * @param pendingNumeric The pending numeric selections
   * @returns An object containing the categorical and numeric facet states, as well as the lucene query
   */
  function buildFacetSelectionState(
    pendingCategorical = pendingCategoricalFacets.value,
    pendingNumeric = pendingNumericFacets.value,
  ): {
    categorical: CategoricalFacetState;
    numeric: NumericFacetSelectionState;
    luceneQuery: string;
  } {
    const categorical: CategoricalFacetState = {};

    Object.entries(pendingCategorical).forEach(
      ([facetKey, selectedBucketKeys]) => {
        const displayState = getCategoricalFacetDisplayState(facetKey);
        categorical[facetKey] = {
          ...displayState,
          selectedBucketKeys,
        };
      },
    );

    const numeric = { ...pendingNumeric };

    return {
      categorical,
      numeric,
      luceneQuery: FacetsUtil.convertFacetSelectionStateToLuceneQuery(
        categorical,
        numeric,
      ),
    };
  }

  // Apply pending categorical selections to the categorical facet state
  const applyFacetSelections = () => {
    const { categorical, numeric, luceneQuery } = buildFacetSelectionState();

    categoricalFacetState.value = categorical;
    numericFacetSelectionState.value = numeric;
    facetSelectionLuceneQuery.value = luceneQuery;
  };

  // Return the amount of rows a user would get should they apply the pending facets.
  // Note that this will not impact the state variables
  const getPendingFacetResultCount = async () => {
    if (
      projectStore.project === undefined ||
      visibleFacetKeys.value.length === 0
    ) {
      facetData.value = undefined;
      return 0;
    }

    const { luceneQuery } = buildFacetSelectionState();
    const displayState = buildDisplayState();

    // Call the API
    const apiResult = await ProjectDataService.getFacetData(
      projectStore.project.id,
      visibleFacetKeys.value,
      {
        queryString: textQuery.value,
        filter: FacetsUtil.concatLuceneQueries(
          luceneQuery,
          additionalLuceneQuery.value,
        ),
        displayState,
      },
    );

    return apiResult.count;
  };

  async function refreshFacetData() {
    if (
      projectStore.project === undefined ||
      visibleFacetKeys.value.length === 0
    ) {
      facetData.value = undefined;
      return;
    }

    const displayState = buildDisplayState();

    // Call the API
    const apiResult = await ProjectDataService.getFacetData(
      projectStore.project.id,
      visibleFacetKeys.value,
      {
        queryString: textQuery.value,
        filter: FacetsUtil.concatLuceneQueries(
          facetSelectionLuceneQuery.value,
          additionalLuceneQuery.value,
        ),
        displayState,
      },
    );
    clearIncludesFromDisplayState();

    facetData.value = {
      count: Object.keys(apiResult.facets).length,
      facets: {
        ...apiResult.facets,
      },
      maxUniqueValueCount: apiResult.maxUniqueValueCount,
    };
  }

  // When the combined query state (facet selections + search bar text) changes or the sort/limit
  //  state within a facet changes, fetch updated facet data.
  watch(
    [
      () => projectStore.project,
      visibleFacetKeys,
      facetSelectionLuceneQuery,
      textQuery,
      categoricalFacetState,
    ],
    refreshFacetData,
  );

  const clearSelectionForFacet = (facetKey: string, facetType: FacetType) => {
    if (facetType === FacetType.Categorical) {
      clearSelectedRowsForFacet(facetKey);
    } else if (facetType === FacetType.Numeric) {
      setNumericFacetSelectionState(facetKey, null);
    } else {
      Logger.error(
        `Attempting to clear selection for facet ${facetKey} with unsupported type ${facetType}.`,
      );
    }
    applyFacetSelections();
  };

  const clearAllSelections = () => {
    clearAllCategoricalFacetSelections();
    clearAllNumericFacetSelections();
    applyFacetSelections();
  };

  // Delete all facet selections (both pending and applied)
  const deleteFacetSelections = async (confirm: any) => {
    const projectId = projectStore.project?.id;
    if (projectId === undefined) {
      Logger.error(
        'Attempted to delete facet selections when no project is loaded.',
      );
      return;
    }
    const { documentIds } = projectStore;
    if (documentIds.length === 0) {
      Logger.error(
        'Attempted to delete facet selections when no documents are loaded.',
      );
      return;
    }

    // Build the deletion criteria from both pending and applied facets
    const deletionCriteria: PropertyValueDeletionCriteria[] = [];
    const { categorical, numeric } = buildFacetSelectionState();

    // Categorical facets
    Object.entries(categorical)
      .filter(
        ([propertyKey]) =>
          propertyKey !== 'set' && propertyKey !== 'provenance_source',
      )
      .forEach(property => {
        const [propertyKey, { selectedBucketKeys }] = property;
        selectedBucketKeys
          .filter(value => value !== 'N/A')
          .forEach(exactValue =>
            deletionCriteria.push({ propertyKey, exactValue }),
          );
      });

    // Numeric facets
    Object.entries(numeric)
      .filter(
        ([propertyKey]) =>
          propertyKey !== 'set' && propertyKey !== 'provenance_source',
      )
      .forEach(property => {
        const [propertyKey, { intervalMin, intervalMax }] = property;
        deletionCriteria.push({
          propertyKey,
          minValue: intervalMin.toString(),
          maxValue: intervalMax.toString(),
        });
      });

    if (deletionCriteria.length === 0) {
      Logger.error('No facet selections to delete.');
      return;
    }

    // Show confirmation dialog before proceeding
    const confirmed = await new Promise<boolean>(resolve => {
      confirm.require({
        message: `This will permanently delete the selected property values from ${documentIds.length} document${documentIds.length !== 1 ? 's' : ''}. This action cannot be undone.`,
        header: `Delete ${deletionCriteria.length} property value${deletionCriteria.length !== 1 ? 's' : ''}?`,
        icon: 'pi pi-exclamation-triangle',
        rejectLabel: 'Cancel',
        acceptLabel: 'Delete',
        accept: () => resolve(true),
        reject: () => resolve(false),
      });
    });

    if (!confirmed) {
      return;
    }

    const request: BulkPropertyValueDeletionRequest = {
      documentIds,
      deletionCriteria,
    };

    try {
      isDeletingFacetSelections.value = true;

      // Call the API to remove all properties values selected
      const response = await PanteraAPI.bulkRemovePropertyValues(
        projectId,
        request,
      );

      Logger.info(
        `Started bulk property removal with task ID: ${response.taskId}`,
      );

      // Wait for the Elasticsearch task to complete before refreshing facet data
      await pollTaskCompletion(response.taskId);

      // Clear all selections after successful deletion
      clearAllSelections();

      refreshFacetData();
    } finally {
      isDeletingFacetSelections.value = false;
    }
  };

  const hasPendingChanges = computed(
    () =>
      Object.entries(pendingCategoricalFacets.value).some(
        ([facetKey, selectedBucketKeys]) =>
          !_.isEqual(
            selectedBucketKeys,
            categoricalFacetState.value[facetKey]?.selectedBucketKeys ?? [],
          ),
      ) ||
      Object.entries(pendingNumericFacets.value).some(
        ([facetKey, selectedInterval]) =>
          !_.isEqual(
            selectedInterval,
            numericFacetSelectionState.value[facetKey] ?? null,
          ),
      ),
  );

  /**
   * Checks whether there are any selected facets, either pending or applied.
   */
  const hasSelectedFacets = computed(
    () =>
      Object.values(pendingCategoricalFacets.value).some(
        values => values.length > 0,
      ) ||
      Object.keys(pendingNumericFacets.value).length > 0 ||
      Object.values(categoricalFacetState.value).some(
        state => state.selectedBucketKeys.length > 0,
      ) ||
      Object.keys(numericFacetSelectionState.value).length > 0,
  );

  const numPendingChanges = computed(
    () =>
      Object.values(pendingCategoricalFacets.value).reduce(
        (sum, selectedBucketKeys) => sum + selectedBucketKeys.length,
        0,
      ) + Object.keys(pendingNumericFacets.value).length,
  );

  /**
   * An array of objects with enough information to summarize the facet selection state.
   * Used to render the chips at the top of the facet panel.
   */
  const facetSelectionsList = computed<FacetSelectionListItem[]>(() => {
    const facetsThatRequireDateFormatting = Object.entries(
      facetData.value?.facets ?? {},
    )
      .filter(
        ([, data]) =>
          data.properties.length > 0 &&
          data.properties[0].tags.includes(PropertyTag.Date),
      )
      .map(([key]) => key);
    return FacetsUtil.convertFacetSelectionStateToList(
      categoricalFacetState.value,
      numericFacetSelectionState.value,
      facetsThatRequireDateFormatting,
      facetMetadata.value,
    );
  });

  return {
    setIsFacetExpanded,
    isFacetExpanded,
    facetData,
    categoricalFacetState,
    getCategoricalFacetDisplayState,
    setCategoricalFacetQueryState,
    setCategoricalFacetSortState,
    setIsRowSelected,
    numericFacetSelectionState,
    setNumericFacetSelectionState,
    setAdditionalFilter,
    isHistogramFacet,
    facetSelectionLuceneQuery,
    facetSelectionsList,
    clearSelectionForFacet,
    clearAllSelections,
    visibleFacetKeys,
    setVisibleFacetKeys,
    setCategoricalFacetState,
    setPendingCategoricalFacets,
    isDeletingFacetSelections,
    setNumericFacetState,
    refreshFacetData,
    pendingCategoricalFacets,
    pendingNumericFacets,
    getCategoricalFacetBuckets,
    applyFacetSelections,
    deleteFacetSelections,
    hasPendingChanges,
    numPendingChanges,
    hasSelectedFacets,
    getPendingFacetResultCount,
  };
}

