  const request: BulkPropertyValueDeletionRequest = {
    documentIds,
    deletionCriteria,
  };

  try {
    isDeletingFacetSelections.value = true;

    // Call the API to remove all properties values selected
    const response = await PanteraAPI.bulkRemovePropertyValues(
      projectId,
      request,
    );

    Logger.info(
      `Started bulk property removal with task ID: ${response.taskId}`,
    );

    // Wait for the Elasticsearch task to complete before refreshing facet data
    const result: TaskCompletionResult = await pollTaskCompletion(
      response.taskId,
    );

    if (!result.success) {
      if (result.timedOut) {
        toast.add({
          severity: 'error',
          summary: 'Deletion Timeout',
          detail:
            'The deletion operation timed out. Please refresh and verify if changes were applied.',
          life: 5000,
        });
      } else if (result.error) {
        toast.add({
          severity: 'error',
          summary: 'Deletion Failed',
          detail: `Failed to delete property values: ${result.error.message}`,
          life: 5000,
        });
      }
      return;
    }

    // Clear all selections after successful deletion
    clearAllSelections();

    refreshFacetData();

    toast.add({
      severity: 'success',
      summary: 'Deletion Complete',
      detail: `Successfully deleted ${deletionCriteria.length} property value${deletionCriteria.length !== 1 ? 's' : ''} from ${documentIds.length} document${documentIds.length !== 1 ? 's' : ''}.`,
      life: 3000,
    });
  } catch (error) {
    Logger.error('Error during facet selection deletion:', error);
    toast.add({
      severity: 'error',
      summary: 'Deletion Failed',
      detail:
        error instanceof Error
          ? error.message
          : 'An unexpected error occurred during deletion.',
      life: 5000,
    });
  } finally {
    isDeletingFacetSelections.value = false;
  }
};

