import { describe, it, expect, vi, beforeEach } from 'vitest';
import { ref } from 'vue';
import useFacets from '@/composables/useFacets';
import PanteraAPI from '@/services/PanteraAPI';
import pollTaskCompletion from '@/util/Tasks';
import useProjectStore from '@/stores/ProjectStore';

vi.mock('@/services/PanteraAPI');
vi.mock('@/util/Tasks');
vi.mock('@/stores/ProjectStore');
vi.mock('@/services/Logger');
vi.mock('primevue/usetoast', () => ({
  useToast: () => ({
    add: vi.fn(),
  }),
}));

describe('useFacets - deleteFacetSelections', () => {
  let textQuery: any;
  let facetMetadata: any;
  let mockConfirm: any;
  let mockProjectStore: any;

  beforeEach(() => {
    vi.clearAllMocks();

    textQuery = ref('');
    facetMetadata = ref({});

    mockConfirm = {
      require: vi.fn(),
    };

    mockProjectStore = {
      project: { id: 'test-project-123' },
      documentIds: ['doc1', 'doc2', 'doc3'],
      getPropertyDescriptorsFromKey: vi.fn(),
    };
    vi.mocked(useProjectStore).mockReturnValue(mockProjectStore as any);
  });

  it('should not proceed if no project is loaded', async () => {
    mockProjectStore.project = undefined;
    const facets = useFacets(textQuery, facetMetadata);

    await facets.deleteFacetSelections(mockConfirm);

    expect(mockConfirm.require).not.toHaveBeenCalled();
    expect(PanteraAPI.bulkRemovePropertyValues).not.toHaveBeenCalled();
  });

  it('should not proceed if no documents are loaded', async () => {
    mockProjectStore.documentIds = [];
    const facets = useFacets(textQuery, facetMetadata);

    await facets.deleteFacetSelections(mockConfirm);

    expect(mockConfirm.require).not.toHaveBeenCalled();
    expect(PanteraAPI.bulkRemovePropertyValues).not.toHaveBeenCalled();
  });

  it('should not proceed if no deletion criteria exist', async () => {
    const facets = useFacets(textQuery, facetMetadata);
    // No facet selections made

    await facets.deleteFacetSelections(mockConfirm);

    expect(mockConfirm.require).not.toHaveBeenCalled();
    expect(PanteraAPI.bulkRemovePropertyValues).not.toHaveBeenCalled();
  });

  it('should show confirmation dialog with correct message', async () => {
    const facets = useFacets(textQuery, facetMetadata);
    facets.setIsRowSelected('author', 'John Doe', true);

    mockConfirm.require.mockImplementation(({ reject }) => reject());

    await facets.deleteFacetSelections(mockConfirm);

    expect(mockConfirm.require).toHaveBeenCalledWith(
      expect.objectContaining({
        message: expect.stringContaining('3 documents'),
        header: expect.stringContaining('Delete 1 property value?'),
        icon: 'pi pi-exclamation-triangle',
        rejectLabel: 'Cancel',
        acceptLabel: 'Delete',
      }),
    );
  });

  it('should show plural form in confirmation for multiple criteria', async () => {
    const facets = useFacets(textQuery, facetMetadata);
    facets.setIsRowSelected('author', 'John Doe', true);
    facets.setIsRowSelected('author', 'Jane Smith', true);

    mockConfirm.require.mockImplementation(({ reject }) => reject());

    await facets.deleteFacetSelections(mockConfirm);

    expect(mockConfirm.require).toHaveBeenCalledWith(
      expect.objectContaining({
        header: expect.stringContaining('Delete 2 property values?'),
      }),
    );
  });

  it('should not proceed if user cancels confirmation', async () => {
    const facets = useFacets(textQuery, facetMetadata);
    facets.setIsRowSelected('author', 'John Doe', true);

    mockConfirm.require.mockImplementation(({ reject }) => reject());

    await facets.deleteFacetSelections(mockConfirm);

    expect(PanteraAPI.bulkRemovePropertyValues).not.toHaveBeenCalled();
    expect(facets.isDeletingFacetSelections.value).toBe(false);
  });

  it('should exclude set and provenance_source properties from deletion', async () => {
    const facets = useFacets(textQuery, facetMetadata);
    facets.setIsRowSelected('set', 'SetA', true);
    facets.setIsRowSelected('provenance_source', 'manual', true);
    facets.setIsRowSelected('author', 'John Doe', true);

    mockConfirm.require.mockImplementation(({ accept }) => accept());
    vi.mocked(PanteraAPI.bulkRemovePropertyValues).mockResolvedValue({
      taskId: 'node1:123',
    });
    vi.mocked(pollTaskCompletion).mockResolvedValue({
      success: true,
      timedOut: false,
    });

    await facets.deleteFacetSelections(mockConfirm);

    expect(PanteraAPI.bulkRemovePropertyValues).toHaveBeenCalledWith(
      'test-project-123',
      expect.objectContaining({
        documentIds: ['doc1', 'doc2', 'doc3'],
        deletionCriteria: [{ propertyKey: 'author', exactValue: 'John Doe' }],
      }),
    );
  });

  it('should build deletion criteria from categorical selections', async () => {
    const facets = useFacets(textQuery, facetMetadata);
    facets.setIsRowSelected('author', 'John Doe', true);
    facets.setIsRowSelected('status', 'published', true);

    mockConfirm.require.mockImplementation(({ accept }) => accept());
    vi.mocked(PanteraAPI.bulkRemovePropertyValues).mockResolvedValue({
      taskId: 'node1:123',
    });
    vi.mocked(pollTaskCompletion).mockResolvedValue({
      success: true,
      timedOut: false,
    });

    await facets.deleteFacetSelections(mockConfirm);

    expect(PanteraAPI.bulkRemovePropertyValues).toHaveBeenCalledWith(
      'test-project-123',
      expect.objectContaining({
        deletionCriteria: expect.arrayContaining([
          { propertyKey: 'author', exactValue: 'John Doe' },
          { propertyKey: 'status', exactValue: 'published' },
        ]),
      }),
    );
  });

  it('should filter out N/A values from deletion criteria', async () => {
    const facets = useFacets(textQuery, facetMetadata);
    facets.setIsRowSelected('author', 'N/A', true);
    facets.setIsRowSelected('author', 'John Doe', true);

    mockConfirm.require.mockImplementation(({ accept }) => accept());
    vi.mocked(PanteraAPI.bulkRemovePropertyValues).mockResolvedValue({
      taskId: 'node1:123',
    });
    vi.mocked(pollTaskCompletion).mockResolvedValue({
      success: true,
      timedOut: false,
    });

    await facets.deleteFacetSelections(mockConfirm);

    const call = vi.mocked(PanteraAPI.bulkRemovePropertyValues).mock.calls[0];
    const deletionCriteria = call[1].deletionCriteria;

    expect(deletionCriteria).toEqual([
      { propertyKey: 'author', exactValue: 'John Doe' },
    ]);
    expect(deletionCriteria).not.toContainEqual(
      expect.objectContaining({ exactValue: 'N/A' }),
    );
  });

  it('should set loading state during deletion', async () => {
    const facets = useFacets(textQuery, facetMetadata);
    facets.setIsRowSelected('author', 'John Doe', true);

    mockConfirm.require.mockImplementation(({ accept }) => accept());
    vi.mocked(PanteraAPI.bulkRemovePropertyValues).mockResolvedValue({
      taskId: 'node1:123',
    });
    vi.mocked(pollTaskCompletion).mockImplementation(
      () =>
        new Promise(resolve => {
          setTimeout(() => resolve({ success: true, timedOut: false }), 100);
        }),
    );

    const deletePromise = facets.deleteFacetSelections(mockConfirm);

    // Should be loading during deletion
    await vi.waitFor(() => {
      expect(facets.isDeletingFacetSelections.value).toBe(true);
    });

    await deletePromise;

    // Should not be loading after deletion
    expect(facets.isDeletingFacetSelections.value).toBe(false);
  });

  it('should clear selections after successful deletion', async () => {
    const facets = useFacets(textQuery, facetMetadata);
    facets.setIsRowSelected('author', 'John Doe', true);

    mockConfirm.require.mockImplementation(({ accept }) => accept());
    vi.mocked(PanteraAPI.bulkRemovePropertyValues).mockResolvedValue({
      taskId: 'node1:123',
    });
    vi.mocked(pollTaskCompletion).mockResolvedValue({
      success: true,
      timedOut: false,
    });

    await facets.deleteFacetSelections(mockConfirm);

    // Selections should be cleared
    expect(facets.pendingCategoricalFacets.value['author']).toEqual([]);
  });

  it('should handle API errors gracefully', async () => {
    const facets = useFacets(textQuery, facetMetadata);
    facets.setIsRowSelected('author', 'John Doe', true);

    mockConfirm.require.mockImplementation(({ accept }) => accept());
    vi.mocked(PanteraAPI.bulkRemovePropertyValues).mockRejectedValue(
      new Error('API error'),
    );

    await facets.deleteFacetSelections(mockConfirm);

    expect(facets.isDeletingFacetSelections.value).toBe(false);
  });

  it('should handle task timeout', async () => {
    const facets = useFacets(textQuery, facetMetadata);
    facets.setIsRowSelected('author', 'John Doe', true);

    mockConfirm.require.mockImplementation(({ accept }) => accept());
    vi.mocked(PanteraAPI.bulkRemovePropertyValues).mockResolvedValue({
      taskId: 'node1:123',
    });
    vi.mocked(pollTaskCompletion).mockResolvedValue({
      success: false,
      timedOut: true,
    });

    await facets.deleteFacetSelections(mockConfirm);

    // Should not clear selections or refresh on timeout
    expect(facets.pendingCategoricalFacets.value['author']).toContain(
      'John Doe',
    );
    expect(facets.isDeletingFacetSelections.value).toBe(false);
  });

  it('should handle task errors', async () => {
    const facets = useFacets(textQuery, facetMetadata);
    facets.setIsRowSelected('author', 'John Doe', true);

    mockConfirm.require.mockImplementation(({ accept }) => accept());
    vi.mocked(PanteraAPI.bulkRemovePropertyValues).mockResolvedValue({
      taskId: 'node1:123',
    });
    vi.mocked(pollTaskCompletion).mockResolvedValue({
      success: false,
      timedOut: false,
      error: new Error('Task failed'),
    });

    await facets.deleteFacetSelections(mockConfirm);

    // Should not clear selections or refresh on error
    expect(facets.pendingCategoricalFacets.value['author']).toContain(
      'John Doe',
    );
    expect(facets.isDeletingFacetSelections.value).toBe(false);
  });

