    await facets.deleteFacetSelections(mockConfirm);

    expect(mockConfirm.require).not.toHaveBeenCalled();
    expect(PanteraAPI.bulkRemovePropertyValues).not.toHaveBeenCalled();
  });

  it('should show confirmation dialog with correct message', async () => {
    const facets = useFacets(textQuery, facetMetadata);
    facets.setIsRowSelected('author', 'John Doe', true);

