    mockConfirm.require.mockImplementation(({ accept }) => accept());
    vi.mocked(PanteraAPI.bulkRemovePropertyValues).mockResolvedValue({
      taskId: 'node1:123',
    });
    vi.mocked(pollTaskCompletion).mockResolvedValue({
      success: false,
      timedOut: false,
      error: new Error('Task failed'),
    });

    await facets.deleteFacetSelections(mockConfirm);

    // Should not clear selections or refresh on error
    expect(facets.pendingCategoricalFacets.value['author']).toContain(
      'John Doe',
    );
    expect(facets.isDeletingFacetSelections.value).toBe(false);
  });

  it('should call pollTaskCompletion with correct task ID', async () => {
    const facets = useFacets(textQuery, facetMetadata);
    facets.setIsRowSelected('author', 'John Doe', true);

    mockConfirm.require.mockImplementation(({ accept }) => accept());
    vi.mocked(PanteraAPI.bulkRemovePropertyValues).mockResolvedValue({
      taskId: 'node1:99999',
    });
    vi.mocked(pollTaskCompletion).mockResolvedValue({
      success: true,
      timedOut: false,
    });

    await facets.deleteFacetSelections(mockConfirm);

    expect(pollTaskCompletion).toHaveBeenCalledWith('node1:99999');
  });
});

