import { describe, it, expect, vi, beforeEach, afterEach } from 'vitest';
import pollTaskCompletion, { TaskCompletionResult } from '@/util/Tasks';
import PanteraAPI from '@/services/PanteraAPI';

vi.mock('@/services/PanteraAPI');
vi.mock('@/services/Logger');

describe('Tasks utility', () => {
  beforeEach(() => {
    vi.clearAllMocks();
    vi.useFakeTimers();
  });

  afterEach(() => {
    vi.useRealTimers();
  });

  describe('pollTaskCompletion', () => {
    it('should resolve with success when task completes', async () => {
      const taskId = 'node1:12345';
      vi.mocked(PanteraAPI.getTask).mockResolvedValue({ completed: true });

      const resultPromise = pollTaskCompletion(taskId, 100, 5000);
      await vi.runAllTimersAsync();
      const result = await resultPromise;

      expect(result.success).toBe(true);
      expect(result.timedOut).toBe(false);
      expect(result.error).toBeUndefined();
      expect(PanteraAPI.getTask).toHaveBeenCalledWith('node1', 12345);
    });

    it('should poll multiple times until task completes', async () => {
      const taskId = 'node1:12345';
      vi.mocked(PanteraAPI.getTask)
        .mockResolvedValueOnce({ completed: false })
        .mockResolvedValueOnce({ completed: false })
        .mockResolvedValueOnce({ completed: true });

      const resultPromise = pollTaskCompletion(taskId, 100, 5000);
      await vi.runAllTimersAsync();
      const result = await resultPromise;

      expect(result.success).toBe(true);
      expect(result.timedOut).toBe(false);
      expect(PanteraAPI.getTask).toHaveBeenCalledTimes(3);
    });

    it('should return timeout status when timeout is reached', async () => {
      const taskId = 'node1:12345';
      vi.mocked(PanteraAPI.getTask).mockResolvedValue({ completed: false });

      const resultPromise = pollTaskCompletion(taskId, 100, 500);
      await vi.runAllTimersAsync();
      const result = await resultPromise;

      expect(result.success).toBe(false);
      expect(result.timedOut).toBe(true);
      expect(result.error).toBeUndefined();
    });

    it('should reject with error for invalid task ID format', async () => {
      const invalidTaskId = 'invalid-task-id';

      await expect(pollTaskCompletion(invalidTaskId)).rejects.toThrow(
        'Invalid task ID format: invalid-task-id',
      );
    });

    it('should handle API errors gracefully', async () => {
      const taskId = 'node1:12345';
      const apiError = new Error('Network error');
      vi.mocked(PanteraAPI.getTask).mockRejectedValue(apiError);

      const resultPromise = pollTaskCompletion(taskId, 100, 5000);
      await vi.runAllTimersAsync();
      const result = await resultPromise;

      expect(result.success).toBe(false);
      expect(result.timedOut).toBe(false);
      expect(result.error).toBe(apiError);
    });

    it('should handle missing node ID', async () => {
      const taskId = ':12345';

      await expect(pollTaskCompletion(taskId)).rejects.toThrow(
        'Invalid task ID format: :12345',
      );
    });

    it('should handle missing task number', async () => {
      const taskId = 'node1:';

      await expect(pollTaskCompletion(taskId)).rejects.toThrow(
        'Invalid task ID format: node1:',
      );
    });

    it('should use default poll interval and timeout', async () => {
      const taskId = 'node1:12345';
      vi.mocked(PanteraAPI.getTask)
        .mockResolvedValueOnce({ completed: false })
        .mockResolvedValueOnce({ completed: true });

      const resultPromise = pollTaskCompletion(taskId); // Using defaults: 500ms interval, 60s timeout
      await vi.advanceTimersByTimeAsync(500);
      const result = await resultPromise;

      expect(result.success).toBe(true);
      expect(PanteraAPI.getTask).toHaveBeenCalledTimes(2);
    });

    it('should parse task number as integer', async () => {
      const taskId = 'node1:99999';
      vi.mocked(PanteraAPI.getTask).mockResolvedValue({ completed: true });

      const resultPromise = pollTaskCompletion(taskId, 100, 5000);
      await vi.runAllTimersAsync();
      await resultPromise;

      expect(PanteraAPI.getTask).toHaveBeenCalledWith('node1', 99999);
    });
  });
});
