// Initialize debug log if it doesn't exist
if (ctx._source.debug_log == null) {
  ctx._source.debug_log = [];
}

for (criteria in params.deletionCriteria) {
  ctx._source.debug_log.add('Processing criteria - propertyKey: ' + criteria.propertyKey + ', exactValue: ' + criteria.exactValue + ', minValue: ' + criteria.minValue + ', maxValue: ' + criteria.maxValue);

  boolean fieldRemoved = false;
  String fullFieldName = null;

  // Check different feature namespaces for the property
  def featureNamespaces = ['keyword_feature', 'long_feature', 'double_feature', 'float_feature', 'boolean_feature', 'analysed_feature', 'date_feature', 'flat_feature'];

  for (namespace in featureNamespaces) {
    fullFieldName = namespace + '.' + criteria.propertyKey;

    if (ctx._source.containsKey(namespace) && ctx._source[namespace] != null && ctx._source[namespace].containsKey(criteria.propertyKey)) {
      def fieldValue = ctx._source[namespace][criteria.propertyKey];
      ctx._source.debug_log.add('Found field: ' + fullFieldName);

      // Handle null values
      if (fieldValue == null) {
        ctx._source.debug_log.add('Field value is null, skipping');
        continue;
      }

      boolean shouldRemove = false;

      // Exact match case
      if (criteria.exactValue != null) {
        if (fieldValue instanceof List) {
          // Handle array values - remove matching items from array
          List originalArray = new ArrayList(fieldValue);
          int originalSize = originalArray.size();
          fieldValue.removeIf(item -> String.valueOf(item).equals(criteria.exactValue));
          int removedCount = originalSize - fieldValue.size();

          if (removedCount > 0) {
            ctx._source.debug_log.add('Array exact match - removed ' + removedCount + ' items matching: ' + criteria.exactValue);

            // If array is now empty, remove the entire field
            if (fieldValue.isEmpty()) {
              shouldRemove = true;
              ctx._source.debug_log.add('Array is now empty, will remove entire field');
            } else {
              fieldRemoved = true; // Mark as processed but don't remove the field
              ctx._source.debug_log.add('Array still has ' + fieldValue.size() + ' remaining items');
            }
          } else {
            ctx._source.debug_log.add('Array exact match - no items found matching: ' + criteria.exactValue);
          }
        } else {
          // Handle single values
          shouldRemove = String.valueOf(fieldValue).equals(criteria.exactValue);
          ctx._source.debug_log.add('Single value exact match check - searching for: ' + criteria.exactValue + ', shouldRemove: ' + shouldRemove);
        }
      } else {
        // Range case - handle arrays and single values
        if (fieldValue instanceof List) {
          // For arrays in range queries, remove items that are in range
          int originalSize = fieldValue.size();
          fieldValue.removeIf(item -> {
            boolean itemInRange = false;
            try {
              def propVal = Double.parseDouble(String.valueOf(item));
              def minVal = criteria.minValue != null ? Double.parseDouble(criteria.minValue) : null;
              def maxVal = criteria.maxValue != null ? Double.parseDouble(criteria.maxValue) : null;

              itemInRange = true;
              if (minVal != null) {
                itemInRange = propVal >= minVal;
              }
              if (itemInRange && maxVal != null) {
                itemInRange = itemInRange && (propVal <= maxVal);
              }
            } catch (Exception e) {
              // Fall back to string comparison for non-numeric values
              String strValue = String.valueOf(item);
              itemInRange = true;
              if (criteria.minValue != null) {
                itemInRange = strValue.compareTo(criteria.minValue) >= 0;
              }
              if (itemInRange && criteria.maxValue != null) {
                itemInRange = itemInRange && (strValue.compareTo(criteria.maxValue) <= 0);
              }
            }
            return itemInRange;
          });

          int removedCount = originalSize - fieldValue.size();
          if (removedCount > 0) {
            ctx._source.debug_log.add('Array range match - removed ' + removedCount + ' items in range');

            // If array is now empty, remove the entire field
            if (fieldValue.isEmpty()) {
              shouldRemove = true;
              ctx._source.debug_log.add('Array is now empty, will remove entire field');
            } else {
              fieldRemoved = true; // Mark as processed but don't remove the field
              ctx._source.debug_log.add('Array still has ' + fieldValue.size() + ' remaining items');
            }
          } else {
            ctx._source.debug_log.add('Array range match - no items found in range');
          }
        } else {
          // Handle single values
          try {
            def propVal = Double.parseDouble(String.valueOf(fieldValue));
            def minVal = criteria.minValue != null ? Double.parseDouble(criteria.minValue) : null;
            def maxVal = criteria.maxValue != null ? Double.parseDouble(criteria.maxValue) : null;

            ctx._source.debug_log.add('Single value numeric comparison - minVal: ' + minVal + ', maxVal: ' + maxVal);

            boolean inRange = true;
            if (minVal != null) {
              inRange = propVal >= minVal;
              ctx._source.debug_log.add('Min value check - inRange: ' + inRange);
            }
            if (inRange && maxVal != null) {
              inRange = inRange && (propVal <= maxVal);
              ctx._source.debug_log.add('Max value check - inRange: ' + inRange);
            }

            shouldRemove = inRange;
            ctx._source.debug_log.add('Single value numeric range result - shouldRemove: ' + shouldRemove);
          } catch (Exception e) {
            ctx._source.debug_log.add('Numeric parsing failed, falling back to string comparison: ' + e.getMessage());

            // Fall back to string comparison for non-numeric values
            String strValue = String.valueOf(fieldValue);
            boolean inRange = true;
            if (criteria.minValue != null) {
              inRange = strValue.compareTo(criteria.minValue) >= 0;
              ctx._source.debug_log.add('String min value check - inRange: ' + inRange);
            }
            if (inRange && criteria.maxValue != null) {
              inRange = inRange && (strValue.compareTo(criteria.maxValue) <= 0);
              ctx._source.debug_log.add('String max value check - inRange: ' + inRange);
            }

            shouldRemove = inRange;
            ctx._source.debug_log.add('Single value string range result - shouldRemove: ' + shouldRemove);
          }
        }
      }

      if (shouldRemove) {
        ctx._source[namespace].remove(criteria.propertyKey);
        ctx._source.debug_log.add('Removed field: ' + fullFieldName);
        fieldRemoved = true;
        break; // Found and removed, no need to check other namespaces
      }
    }
  }

  if (!fieldRemoved) {
    ctx._source.debug_log.add('Field not found in any namespace: ' + criteria.propertyKey);
  }
}

