// Helper function to check if a value is in range
boolean isValueInRange(def value, String minValue, String maxValue) {
  try {
    double numValue = Double.parseDouble(String.valueOf(value));
    double min = minValue != null ? Double.parseDouble(minValue) : Double.NEGATIVE_INFINITY;
    double max = maxValue != null ? Double.parseDouble(maxValue) : Double.POSITIVE_INFINITY;
    return numValue >= min && numValue <= max;
  } catch (Exception e) {
    // Fall back to string comparison for non-numeric values
    String strValue = String.valueOf(value);
    boolean inRange = true;
    if (minValue != null) {
      inRange = strValue.compareTo(minValue) >= 0;
    }
    if (inRange && maxValue != null) {
      inRange = strValue.compareTo(maxValue) <= 0;
    }
    return inRange;
  }
}

for (criteria in params.deletionCriteria) {
  // Validate input parameters
  if (criteria.propertyKey == null || criteria.propertyKey.isEmpty()) {
    continue; // Skip invalid criteria
  }

  // Validate range values if both are provided
  if (criteria.minValue != null && criteria.maxValue != null) {
    try {
      double min = Double.parseDouble(criteria.minValue);
      double max = Double.parseDouble(criteria.maxValue);
      if (min > max) {
        continue; // Skip invalid range
      }
    } catch (Exception e) {
      // For non-numeric values, string comparison will be used
    }
  }

  boolean fieldProcessed = false;
  String fullFieldName = null;

  // Check all feature namespaces that exist in the document
  for (namespace in ctx._source.keySet()) {
    // Only process namespaces that end with '_feature'
    if (!namespace.endsWith('_feature')) {
      continue;
    }

    fullFieldName = namespace + '.' + criteria.propertyKey;

    if (ctx._source[namespace] != null && ctx._source[namespace].containsKey(criteria.propertyKey)) {
      def originalFieldValue = ctx._source[namespace][criteria.propertyKey];

      // Handle null values
      if (originalFieldValue == null) {
        continue;
      }

      // Create defensive copy for arrays to prevent concurrent modification issues
      def fieldValue = originalFieldValue instanceof List ? new ArrayList(originalFieldValue) : originalFieldValue;

      boolean shouldRemove = false;

      // Exact match case
      if (criteria.exactValue != null) {
        if (fieldValue instanceof List) {
          // Handle array values - remove matching items from array
          int originalSize = fieldValue.size();
          fieldValue.removeIf(item -> String.valueOf(item).equals(criteria.exactValue));
          int removedCount = originalSize - fieldValue.size();

          if (removedCount > 0) {
            // Update the source with modified array
            ctx._source[namespace][criteria.propertyKey] = fieldValue;

            // If array is now empty, remove the entire field
            if (fieldValue.isEmpty()) {
              shouldRemove = true;
            } else {
              fieldProcessed = true; // Mark as processed but don't remove the field
            }
          }
        } else {
          // Handle single values
          shouldRemove = String.valueOf(fieldValue).equals(criteria.exactValue);
        }
      } else {
        // Range case - handle arrays and single values
        if (fieldValue instanceof List) {
          // For arrays in range queries, remove items that are in range
          int originalSize = fieldValue.size();
          fieldValue.removeIf(item -> isValueInRange(item, criteria.minValue, criteria.maxValue));
          int removedCount = originalSize - fieldValue.size();

          if (removedCount > 0) {
            // Update the source with modified array
            ctx._source[namespace][criteria.propertyKey] = fieldValue;

            // If array is now empty, remove the entire field
            if (fieldValue.isEmpty()) {
              shouldRemove = true;
            } else {
              fieldProcessed = true; // Mark as processed but don't remove the field
            }
          }
        } else {
          // Handle single values
          shouldRemove = isValueInRange(fieldValue, criteria.minValue, criteria.maxValue);
        }
      }

      if (shouldRemove) {
        ctx._source[namespace].remove(criteria.propertyKey);
        fieldProcessed = true;
        break; // Found and removed, no need to check other namespaces
      }
    }
  }
