@Test
@WithUserDetails(MockUser.ADAM)
public void testItIncludesGroupRolesInUnassigned() throws Exception {
  // Ensure adam has the user role and it's not explicitly assigned (ie/ it's a group role)
  Assertions.assertTrue(userAdam.hasRoleByName("USER"));
  Assertions.assertNotNull(userAdam.getRoles());
  Assertions.assertFalse(
    userAdam.getRoles().stream().map(Role::getName).anyMatch("USER"::equals)
  );

  final ResultActions resultActions = mockMvc
    .perform(
      MockMvcRequestBuilders.get(
        ROOT_URL + "/roles/unassigned/" + userUrsula.getId()
      )
    )
    .andExpect(status().isOk());

  final List<Role> unassignedRoles = getResponseContent(
    resultActions.andReturn(),
    new TypeReference<>() {}
  );
  Assertions.assertNotNull(unassignedRoles);
  Assertions.assertTrue(
    unassignedRoles.stream().noneMatch(role -> role.getName().equals("USER"))
  );
}

