  /**
   * Find the index of a user in a list of users
   * @param user      The user to find
   * @param userList  The list of users to search
   * @return          The index of the user in the list, or -1 if not found
   */
  private static int indexOfUser(final User user, List<User> userList) {
    for (int i = 0; i < userList.size(); i++) {
      if (userList.get(i).getId().equals(user.getId())) {
        return i;
      }
    }
    return -1;
  }

  /**
   * Finds the index of a role in a list of roles
   * @param role      The role to find
   * @param roleList  The list of roles to search
   * @return          The index of the role in the list, or -1 if not found
   */
  private static int indexOfRole(final Role role, List<Role> roleList) {
    for (int i = 0; i < roleList.size(); i++) {
      if (roleList.get(i).getId().equals(role.getId())) {
        return i;
      }
    }
    return -1;
  }

  /**
   * Finds the index of a group in a list of groups
   * @param group     The group to find
   * @param groupList The list of groups to search
   * @return          The index of the group in the list, or -1 if not found
   */
  private static int indexOfGroup(final Group group, List<Group> groupList) {
    for (int i = 0; i < groupList.size(); i++) {
      if (groupList.get(i).getId().equals(group.getId())) {
        return i;
      }
    }
    return -1;
  }
}

