package software.uncharted.pantera.controller;

import static org.springframework.security.test.web.servlet.request.SecurityMockMvcRequestPostProcessors.csrf;
import static org.springframework.test.web.servlet.result.MockMvcResultMatchers.status;

import com.fasterxml.jackson.core.type.TypeReference;
import com.fasterxml.jackson.databind.JsonNode;
import jakarta.annotation.PostConstruct;
import jakarta.transaction.Transactional;
import java.util.Collection;
import java.util.HashSet;
import java.util.List;
import java.util.Map;
import java.util.Set;
import lombok.extern.slf4j.Slf4j;
import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Disabled;
import org.junit.jupiter.api.Test;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.data.domain.Page;
import org.springframework.security.test.context.support.WithUserDetails;
import org.springframework.test.web.servlet.ResultActions;
import org.springframework.test.web.servlet.request.MockMvcRequestBuilders;
import software.uncharted.pantera.PanteraApplicationTests;
import software.uncharted.pantera.annotations.JsonResource;
import software.uncharted.pantera.configuration.MockUser;
import software.uncharted.pantera.model.Group;
import software.uncharted.pantera.model.User;
import software.uncharted.pantera.model.authority.AuthorityLevel;
import software.uncharted.pantera.model.authority.Role;
import software.uncharted.pantera.repository.GroupRepository;
import software.uncharted.pantera.repository.RoleRepository;
import software.uncharted.pantera.repository.UserRepository;
import software.uncharted.pantera.service.GroupService;
import software.uncharted.pantera.service.RoleService;
import software.uncharted.pantera.service.UserService;

@Slf4j
public class AdminControllerTests extends PanteraApplicationTests {

  final String ROOT_URL = "/api/admin";

  @JsonResource("classpath*:/mock-users/admin.json")
  User userAdamJson;

  @JsonResource("classpath*:/mock-users/user.json")
  User userUrsulaJson;

  User userAdam;
  User userUrsula;

  @Override
  @PostConstruct
  public void init() {
    // Create the Everyone group with the USER role if it doesn't exist
    Group everyoneGroup = groupRepository.findByName("Everyone").orElse(null);
    if (everyoneGroup == null) {
      everyoneGroup = new Group();
      everyoneGroup.setName("Everyone");
      everyoneGroup.setDescription("Default group for all users");
      everyoneGroup.setCreatedAtMs(System.currentTimeMillis());

      // Add the USER role to the group
      Role userRole = roleRepository.findByName("USER").orElse(null);
      if (userRole != null) {
        everyoneGroup.setRoles(List.of(userRole));
      }
      everyoneGroup = groupRepository.save(everyoneGroup);
    }

    // Add both users to the Everyone group
    final Group finalEveryoneGroup = everyoneGroup;
    userAdam = userService.getById(userAdamJson.getId());
    if (userAdam.getGroups() == null || !userAdam.getGroups().contains(finalEveryoneGroup)) {
      if (userAdam.getGroups() == null) {
        userAdam.setGroups(new HashSet<>());
      }
      userAdam.getGroups().add(finalEveryoneGroup);
      userAdam = userRepository.save(userAdam);
    }

    userUrsula = userService.getById(userUrsulaJson.getId());
    if (userUrsula.getGroups() == null || !userUrsula.getGroups().contains(finalEveryoneGroup)) {
      if (userUrsula.getGroups() == null) {
        userUrsula.setGroups(new HashSet<>());
      }
      userUrsula.getGroups().add(finalEveryoneGroup);
      userUrsula = userRepository.save(userUrsula);
    }
  }

  @Autowired
  private UserRepository userRepository;

  @Autowired
  private RoleRepository roleRepository;

  @Autowired
  private RoleService roleService;

  @Autowired
  private UserService userService;

  @Autowired
  private GroupService groupService;

  @Autowired
  private GroupRepository groupRepository;

  @Test
  @WithUserDetails(MockUser.URSULA)
  public void testItReturns403OnUnAuthorized() throws Exception {
    mockMvc
      .perform(
        MockMvcRequestBuilders.get(ROOT_URL + "/users/" + userUrsula.getId())
      )
      .andExpect(status().isForbidden());
  }

  @Test
  @WithUserDetails(MockUser.ADAM)
  public void testAdminCanGetOwnDetails() throws Exception {
    final ResultActions resultActions = mockMvc
      .perform(
        MockMvcRequestBuilders.get(ROOT_URL + "/users/" + userAdam.getId())
      )
      .andExpect(status().isOk());

    final User user = getResponseContent(resultActions.andReturn(), User.class);
    Assertions.assertNotNull(user);
    Assertions.assertEquals(userAdam.getId(), user.getId());
  }

  @Test
  @WithUserDetails(MockUser.ADAM)
  public void testItReturns404OnUnknownUsers() throws Exception {
    mockMvc
      .perform(MockMvcRequestBuilders.get(ROOT_URL + "/users/not-a-user-id"))
      .andExpect(status().isNotFound());
  }

  @Test
  @WithUserDetails(MockUser.ADAM)
  public void testItCanPageUsers() throws Exception {
    final long totalUsers = userRepository.count();

    // Get the first page of users with 1 row per page
    final ResultActions resultActions = mockMvc
      .perform(MockMvcRequestBuilders.get(ROOT_URL + "/users?page=0&rows=1"))
      .andExpect(status().isOk());

    // Ensure we got back one element and the total number of pages == total number of users
    final Page<User> users = getResponseContent(
      resultActions.andReturn(),
      new TypeReference<>() {}
    );
    final User user1 = users.getContent().getFirst();
    Assertions.assertNotNull(users);
    Assertions.assertEquals(1, users.getSize());
    Assertions.assertEquals(1, users.getContent().size());
    Assertions.assertEquals(users.getTotalElements(), totalUsers);

    // Grab the second page and ensure the users are different
    final ResultActions resultActions2 = mockMvc
      .perform(MockMvcRequestBuilders.get(ROOT_URL + "/users?page=1&rows=1"))
      .andExpect(status().isOk());

    final Page<User> users2 = getResponseContent(
      resultActions2.andReturn(),
      new TypeReference<>() {}
    );
    final User user2 = users2.getContent().getFirst();
    Assertions.assertNotNull(users2);
    Assertions.assertEquals(1, users2.getSize());
    Assertions.assertEquals(1, users2.getContent().size());

    Assertions.assertNotEquals(user1.getId(), user2.getId());
  }

  @Test
  @WithUserDetails(MockUser.ADAM)
  public void testItCanSortByUsername() throws Exception {
    final ResultActions resultActions = mockMvc
      .perform(
        MockMvcRequestBuilders.get(
          ROOT_URL + "/users?page=0&rows=5&sortField=username&sortOrder=1"
        )
      )
      .andExpect(status().isOk());

    final Page<User> users = getResponseContent(
      resultActions.andReturn(),
      new TypeReference<>() {}
    );
    Assertions.assertNotNull(users);
    Assertions.assertTrue(users.getContent().size() < 5);
    Assertions.assertTrue(
      indexOfUser(userAdam, users.getContent()) <
      indexOfUser(userUrsula, users.getContent())
    );
  }

  @Test
  @WithUserDetails(MockUser.ADAM)
  public void testItCanSortByUsernameDescending() throws Exception {
    final ResultActions resultActions = mockMvc
      .perform(
        MockMvcRequestBuilders.get(
          ROOT_URL + "/users?page=0&rows=5&sortField=username&sortOrder=-1"
        )
      )
      .andExpect(status().isOk());

    final Page<User> users = getResponseContent(
      resultActions.andReturn(),
      new TypeReference<>() {}
    );
    Assertions.assertNotNull(users);
    Assertions.assertTrue(users.getContent().size() < 5);
    Assertions.assertTrue(
      indexOfUser(userAdam, users.getContent()) >
      indexOfUser(userUrsula, users.getContent())
    );
  }

  @Test
  @WithUserDetails(MockUser.ADAM)
  public void testItCanFilterByUsername() throws Exception {
    final ResultActions resultActions = mockMvc
      .perform(
        MockMvcRequestBuilders.get(
          ROOT_URL + "/users?page=0&rows=5&queryString=adam"
        )
      )
      .andExpect(status().isOk());

    final Page<User> users = getResponseContent(
      resultActions.andReturn(),
      new TypeReference<>() {}
    );
    Assertions.assertNotNull(users);
    Assertions.assertEquals(1, users.getContent().size());
    Assertions.assertEquals(
      userAdam.getId(),
      users.getContent().getFirst().getId()
    );
  }

  @Test
  @WithUserDetails(MockUser.ADAM)
  public void testItCanPageRoles() throws Exception {
    final long totalRoles = roleRepository.count();

    // Get the first page of roles with 1 row per page
    final ResultActions resultActions = mockMvc
      .perform(MockMvcRequestBuilders.get(ROOT_URL + "/roles?page=0&rows=1"))
      .andExpect(status().isOk());

    // Ensure we got back one element and the total number of pages == total number of users
