package software.uncharted.pantera.service;

import co.elastic.clients.elasticsearch.core.BulkResponse;
import com.fasterxml.jackson.core.JsonProcessingException;
import com.fasterxml.jackson.databind.ObjectMapper;
import java.io.IOException;
import java.util.Collection;
import java.util.List;
import java.util.stream.Collectors;
import java.util.stream.Stream;
import lombok.RequiredArgsConstructor;
import org.springframework.stereotype.Service;
import software.uncharted.elasticsearch.ManagedElasticsearchService;
import software.uncharted.pantera.model.Group;
import software.uncharted.pantera.model.project.Project;
import software.uncharted.pantera.model.search.PropertyDescriptor;

@Service
@RequiredArgsConstructor
public class ProjectBootstrapService {

  final ManagedElasticsearchService elasticsearchService;
  final ProjectService projectService;
  final GroupService groupService;
  final ObjectMapper objectMapper;
  final PropertyDescriptorService propertyDescriptorService;

  /**
   * Bootstrap a project with the given data. The data assumes a JSONL format.
   * @param data          The data to bootstrap the project with
   * @return              The project
   * @throws IOException  If an error occurs while creating the project
   */
  public Project bootstrap(final String[] data) throws IOException {
    return bootstrap(data, List.of(), true);
  }

  /**
   * Bootstrap a project with the given data. The data assumes a JSONL format.
   * @param data                The data to bootstrap the project with
   * @param propertyDescriptors The property descriptors to add to the project
   * @return                    The project
   * @throws IOException        If an error occurs while creating the project
   */
  public Project bootstrap(
    final String[] data,
    final List<PropertyDescriptor> propertyDescriptors
  ) throws IOException {
    return bootstrap(data, propertyDescriptors, true);
  }

  /**
   * Bootstrap a project with the given data. The data assumes a JSONL format.
   *
   * @param data                    The data to bootstrap the project with
   * @param propertyDescriptors     The property descriptors to add to the project
   * @param initializeDefaultGroups Whether to initialize default groups
   * @return The project
   * @throws IOException If an error occurs while creating the project
   */
  public Project bootstrap(
    final String[] data,
    final List<PropertyDescriptor> propertyDescriptors,
    final boolean initializeDefaultGroups
  ) throws IOException {
    final Project project = projectService.create(
      new Project().setName("test").setDescription("test")
    );
    elasticsearchService.createIndex(project.getIndexName());
    final BulkResponse response = insert(project, data);
    if (response != null && response.errors()) {
      throw new RuntimeException("Error inserting data into Elasticsearch");
    }
    if (initializeDefaultGroups) {
      final Collection<Group> defaultGroups = groupService.getDefaultGroups();
      projectService.addGroups(project, defaultGroups);
    }
    propertyDescriptors.forEach(propertyDescriptor ->
      propertyDescriptor.setProject(project)
    );
    propertyDescriptorService.create(project, propertyDescriptors);
    return project;
  }

  public BulkResponse insert(final Project project, final String[] data)
    throws IOException {
    if (data != null && data.length > 0) {
      final BulkResponse response = elasticsearchService.insert(
        project.getIndexName(),
        Stream.of(data)
          .map(String::trim)
          .map(str -> {
            try {
              return objectMapper.readTree(str);
            } catch (final JsonProcessingException e) {
              throw new RuntimeException(e);
            }
          })
          .collect(Collectors.toList())
      );

