  1038  @Test
  1039  @WithUserDetails(MockUser.ADAM)
  1040  @Transactional
  1041  void testItCanGetMultiplePermissionsAtOnce() throws Exception {
  1042    final int numProjects = 10;
  1043    final Project[] projects = new Project[numProjects];
  1044    for (int i = 0; i < numProjects; i++) {
  1045      projects[i] = projectService.create(
  1046        new Project()
  1047          .setName("Project %s".formatted(i))
  1048          .setDescription("Project %s description".formatted(i))
  1049      );
  1050    }
  1051
  1052    // Give ursula owner permissions on all projects and give us read to even projects and write to odd projects
  1053    final User ursula = userService.getByUsername(MockUser.URSULA);
  1054    for (int i = 0; i < numProjects; i++) {
  1055      projectService.addUser(projects[i], ursula, ProjectPermissionLevel.OWNER);
  1056      final ProjectPermissionLevel level = i % 2 == 0
  1057        ? ProjectPermissionLevel.READ
  1058        : ProjectPermissionLevel.WRITE;
  1059      projectService.updateUser(
  1060        projects[i],
  1061        userService.getByUsername(MockUser.ADAM),
  1062        level
  1063      );
  1064    }
  1065
  1066    ResultActions resultActions = mockMvc
  1067      .perform(
  1068        MockMvcRequestBuilders.post(ROOT_URL + "/permissions/levels")
  1069          .header("content-type", "application/json")
  1070          .content(
  1071            objectMapper.writeValueAsString(
  1072              Stream.of(projects)
  1073                .map(Project::getId)
  1074                .collect(Collectors.toList())
  1075            )
  1076          )
  1077          .with(csrf())
  1078      )
  1079      .andExpect(status().isOk());
  1080
  1081    final Map<String, ProjectPermissionLevel> permissions = getResponseContent(
  1082      resultActions.andReturn(),
  1083      new TypeReference<>() {}
  1084    );
  1085    // Adam is an admin, so he should have OWNER permissions on all projects regardless of what we try to set
  1086    for (int i = 0; i < numProjects; i++) {
  1087      Assertions.assertEquals(
  1088        ProjectPermissionLevel.OWNER,
  1089        permissions.get(projects[i].getId())
  1090      );
  1091    }
  1092  }
