  @Test
  @WithUserDetails(MockUser.ADAM)
  @Transactional
  void testItCanGetMultiplePermissionsAtOnce() throws Exception {
    final int numProjects = 10;
    final Project[] projects = new Project[numProjects];
    for (int i = 0; i < numProjects; i++) {
      projects[i] = projectService.create(
        new Project()
          .setName("Project %s".formatted(i))
          .setDescription("Project %s description".formatted(i))
      );
    }

    // Give ursula owner permissions on all projects and give us read to even projects and write to odd projects
    final User ursula = userService.getByUsername(MockUser.URSULA);
    for (int i = 0; i < numProjects; i++) {
      projectService.addUser(projects[i], ursula, ProjectPermissionLevel.OWNER);
      final ProjectPermissionLevel level = i % 2 == 0
        ? ProjectPermissionLevel.READ
        : ProjectPermissionLevel.WRITE;
      projectService.updateUser(
        projects[i],
        userService.getByUsername(MockUser.ADAM),
        level
      );
    }

    ResultActions resultActions = mockMvc
      .perform(
        MockMvcRequestBuilders.post(ROOT_URL + "/permissions/levels")
          .header("content-type", "application/json")
          .content(
            objectMapper.writeValueAsString(
              Stream.of(projects)
                .map(Project::getId)
                .collect(Collectors.toList())
            )
          )
          .with(csrf())
      )
      .andExpect(status().isOk());

    final Map<String, ProjectPermissionLevel> permissions = getResponseContent(
      resultActions.andReturn(),
      new TypeReference<>() {}
    );
    // Adam is an admin, so he should have OWNER permissions on all projects regardless of what we try to set
    for (int i = 0; i < numProjects; i++) {
      Assertions.assertEquals(
        ProjectPermissionLevel.OWNER,
        permissions.get(projects[i].getId())
      );
    }
  }

