  @Test
  @WithUserDetails(MockUser.ADAM)
  @Transactional
  void testItCanGetAPageOfProjectsForAUser() throws Exception {
    // Adam creates 3 projects
    final Project project1 = projectService.create(
      new Project().setName("Project 1").setDescription("Project 1 description")
    );
    final Project project2 = projectService.create(
      new Project().setName("Project 2").setDescription("Project 2 description")
    );

    final Project project3 = projectService.create(
      new Project().setName("Project 3").setDescription("Project 3 description")
    );

    // Add urusla to project 1 and make her the owner
    final User ursula = userService.getByUsername(MockUser.URSULA);
    projectService.addUser(project1, ursula, ProjectPermissionLevel.OWNER);
    final User adam = userService.getByUsername(MockUser.ADAM);
    projectService.removeUsers(project1, Set.of(adam));

    // Adam should see the 3 projects he created (as admin he sees all projects in the system)
    // Get the first page of projects with 1 row per page
    final ResultActions resultActions = mockMvc
      .perform(MockMvcRequestBuilders.get(ROOT_URL + "?page=0&rows=1"))
      .andExpect(status().isOk());

    // Ensure we got back one element and the total number of pages == total number of projects
    final Page<Project> projects = getResponseContent(
      resultActions.andReturn(),
      new TypeReference<>() {}
    );
    Assertions.assertEquals(3, projects.getTotalElements());
    Assertions.assertEquals(3, projects.getTotalPages());
    Assertions.assertEquals(1, projects.getNumberOfElements());
    Assertions.assertEquals(0, projects.getNumber());
    Assertions.assertEquals(1, projects.getSize());
    // Adam as admin should see all projects, including the one he was removed from
  }

