  @Test
  @WithUserDetails(MockUser.ADAM)
  @Transactional
  void testFilterIsCaseInsensitive() throws Exception {
    // Adam creates 3 projects
    final Project project1 = projectService.create(
      new Project()
        .setName("PROJECT 1 NAME")
        .setDescription("description number one")
    );

    final ResultActions descriptionResultActions = mockMvc
      .perform(
        MockMvcRequestBuilders.get(ROOT_URL)
          .param("page", "0")
          .param("rows", "1")
          .param("queryString", "DESCRiption NUMBer ONE")
      )
      .andExpect(status().isOk());

    final Page<Project> descriptionProjectResults = getResponseContent(
      descriptionResultActions.andReturn(),
      new TypeReference<>() {}
    );
    Assertions.assertEquals(1, descriptionProjectResults.getTotalElements());
    Assertions.assertEquals(1, descriptionProjectResults.getTotalPages());
    Assertions.assertEquals(1, descriptionProjectResults.getNumberOfElements());
    Assertions.assertEquals(0, descriptionProjectResults.getNumber());
    Assertions.assertEquals(1, descriptionProjectResults.getSize());
    Assertions.assertEquals(
      project1.getId(),
      descriptionProjectResults.get().findFirst().get().getId()
    );

    final ResultActions nameResultActions = mockMvc
      .perform(
        MockMvcRequestBuilders.get(ROOT_URL)
          .param("page", "0")
          .param("rows", "1")
          .param("queryString", "proJECT 1 nAmE")
      )
      .andExpect(status().isOk());

    final Page<Project> nameProjectResults = getResponseContent(
      nameResultActions.andReturn(),
      new TypeReference<>() {}
    );
    Assertions.assertEquals(1, nameProjectResults.getTotalElements());
    Assertions.assertEquals(1, nameProjectResults.getTotalPages());
    Assertions.assertEquals(1, nameProjectResults.getNumberOfElements());
    Assertions.assertEquals(0, nameProjectResults.getNumber());
    Assertions.assertEquals(1, nameProjectResults.getSize());
    Assertions.assertEquals(
      project1.getId(),
      nameProjectResults.get().findFirst().get().getId()
    );

    final ResultActions emptyResultActions = mockMvc
      .perform(
        MockMvcRequestBuilders.get(ROOT_URL)
          .param("page", "0")
          .param("rows", "1")
          .param("queryString", "no matches here")
      )
      .andExpect(status().isOk());

    final Page<Project> emptyProjectResults = getResponseContent(
      emptyResultActions.andReturn(),
      new TypeReference<>() {}
    );
    Assertions.assertTrue(emptyProjectResults.isEmpty());
  }

