  void testItCanUpdateAProject() throws Exception {
    final Project project = projectService.create(
      new Project()
        .setName("Project Name")
        .setDescription("Project description")
    );

    final Project clone = objectMapper
      .readValue(objectMapper.writeValueAsString(project), Project.class)
      .setName("Updated Name");

    final ResultActions resultActions = mockMvc
      .perform(
        MockMvcRequestBuilders.put(ROOT_URL + "/" + project.getId())
          .contentType("application/json")
          .content(objectMapper.writeValueAsString(clone))
          .with(csrf())
      )
      .andExpect(status().isOk());

    final Project updatedProject = getResponseContent(
      resultActions.andReturn(),
      new TypeReference<>() {}
    );
    Assertions.assertEquals(clone.getId(), updatedProject.getId());
    Assertions.assertEquals(clone.getName(), updatedProject.getName());
  }

