  void testItCanAddUsersToAProject() throws Exception {
    final Project project = projectService.create(
      new Project()
        .setName("Project Name")
        .setDescription("Project description")
    );

    final User ursula = userService.getByUsername(MockUser.URSULA);

    final ResultActions resultActions = mockMvc
      .perform(
        MockMvcRequestBuilders.post(
          ROOT_URL + "/" + project.getId() + "/permissions/user"
        )
          .contentType("application/json")
          .content(objectMapper.writeValueAsString(List.of(ursula.getId())))
          .with(csrf())
      )
      .andExpect(status().isOk());

    final ProjectPermission updatedPermissions = getResponseContent(
      resultActions.andReturn(),
      new TypeReference<>() {}
    );
    Assertions.assertEquals(project.getId(), updatedPermissions.getProjectId());
    Assertions.assertEquals(2, updatedPermissions.getUserPermissions().size());
    Assertions.assertTrue(
      updatedPermissions
        .getUserPermissions()
        .stream()
        .anyMatch(userPermission ->
          userPermission.getUser().getUsername().equals(MockUser.ADAM)
        )
    );
    Assertions.assertTrue(
      updatedPermissions
        .getUserPermissions()
        .stream()
        .anyMatch(userPermission ->
          userPermission.getUser().getUsername().equals(MockUser.URSULA)
        )
    );
  }

