package software.uncharted.crawlimporter.service;

import jakarta.annotation.PostConstruct;
import java.io.File;
import java.io.IOException;
import java.nio.file.Files;
import java.security.MessageDigest;
import java.security.NoSuchAlgorithmException;
import java.util.List;
import java.util.Map;
import lombok.RequiredArgsConstructor;
import lombok.extern.slf4j.Slf4j;
import org.springframework.context.annotation.DependsOn;
import org.springframework.http.HttpEntity;
import org.springframework.http.HttpHeaders;
import org.springframework.http.HttpMethod;
import org.springframework.http.MediaType;
import org.springframework.stereotype.Service;
import org.springframework.web.client.RestTemplate;
import software.uncharted.crawlimporter.configuration.CrawlImporterConfig;
import software.uncharted.crawlimporter.model.CrawlFileElement;
import software.uncharted.crawlimporter.model.UploadedCrawlFile;

@Service
@Slf4j
@RequiredArgsConstructor
@DependsOn("atlantisRestTemplate") // Replace with the name of the dependent bean
public class CrawlFileService {

  private final CrawlImporterConfig config;
  private final RestTemplate atlantisRestTemplate;
  private final RestTemplate restTemplate;
  private String IMPORT_BUCKET;

  @PostConstruct
  public void init() {
    // Initialize the IMPORT_BUCKET variable here
    this.IMPORT_BUCKET = atlantisRestTemplate.getForObject(
      "/api/document/bucket",
      String.class
    );
  }

  public static String sha256Hex(byte[] data) {
    try {
      MessageDigest digest = MessageDigest.getInstance("SHA-256");
      byte[] hashBytes = digest.digest(data);
      return bytesToHexLower(hashBytes);
    } catch (NoSuchAlgorithmException e) {
      throw new RuntimeException("SHA-256 algorithm not available", e);
    }
  }

  private static String bytesToHexLower(byte[] bytes) {
    StringBuilder hexString = new StringBuilder(2 * bytes.length);
    for (byte b : bytes) {
      hexString.append(Character.forDigit((b >> 4) & 0xF, 16));
      hexString.append(Character.forDigit(b & 0xF, 16));
    }
    return hexString.toString();
  }

  public UploadedCrawlFile uploadFileElement(
    final CrawlFileElement crawlFileElement
  ) {
    final File file = crawlFileElement.getFile(config.getRootCrawlPath());
    if (!file.exists()) {
      log.error("File does not exist: {}", file.getPath());
      return null;
    }
    final byte[] fileBytes;
    try {
      fileBytes = Files.readAllBytes(file.toPath());
    } catch (IOException e) {
      log.error("Error reading file: {}", file.getPath(), e);
      return null;
    }

    final String[] crawlFilePieces = crawlFileElement.getPath().split("/");
    final String key =
      config.getProjectId() + "/" + crawlFilePieces[crawlFilePieces.length - 1];
    try {
      atlantisRestTemplate.put(
        "/api/file?bucket=%s&key=%s&id=google".formatted(IMPORT_BUCKET, key),
        fileBytes
      );
    } catch (Exception e) {
      log.error("Error uploading file: {}", crawlFileElement.getPath(), e);
      return null;
    }

    return new UploadedCrawlFile()
      .setKey(key)
      .setSizeInBytes(fileBytes.length)
      .setSha256Hash(sha256Hex(fileBytes))
      .setFilename(file.getName());
  }
}

