  private final CrawlImporterConfig config;

  @Bean(name = "atlantisRestTemplate")
  public RestTemplate atlantisRestTemplate() {
    RestTemplate restTemplate = new RestTemplate();
    restTemplate.setUriTemplateHandler(
      new DefaultUriBuilderFactory(config.getBaseUrl())
    );

    // Add an interceptor to include headers in all requests
    restTemplate.setInterceptors(
      Collections.singletonList((request, body, execution) -> {
        request
          .getHeaders()
          .set(
            "Authorization",
            "Bearer " + atlantisTokenService.getAccessToken()
          );
        return execution.execute(request, body);
      })
    );

    return restTemplate;
  }
}

