package software.uncharted.crawlimporter.service;

import com.fasterxml.jackson.core.JsonProcessingException;
import com.fasterxml.jackson.databind.JsonMappingException;
import com.fasterxml.jackson.databind.JsonNode;
import com.fasterxml.jackson.databind.ObjectMapper;
import java.util.Map;
import lombok.RequiredArgsConstructor;
import org.springframework.http.ResponseEntity;
import org.springframework.stereotype.Service;
import org.springframework.web.client.RestTemplate;
import software.uncharted.crawlimporter.configuration.CrawlImporterConfig;

@Service
@RequiredArgsConstructor
public class AtlantisTokenService {

  private final CrawlImporterConfig config;
  private final RestTemplate restTemplate;
  private final ObjectMapper objectMapper;

  private String token = null;

  private Long expiresIn() {
    if (token == null) {
      return 0L;
    }
    final String[] parts = token.split("\\.");
    if (parts.length != 3) {
      return 0L;
    }
    try {
      final String payload = new String(
        java.util.Base64.getDecoder().decode(parts[1])
      );
      final JsonNode jwtNode = objectMapper.readValue(payload, JsonNode.class);
      final Long expiresAtSeconds = jwtNode.at("/exp").asLong();
      final Long currentTimeSeconds = System.currentTimeMillis() / 1000;
      return expiresAtSeconds - currentTimeSeconds;
    } catch (JsonProcessingException e) {
      return 0L;
    }
  }

  public String getAccessToken() {
    if (token != null && expiresIn() > 60) {
      return token;
    }

    final ResponseEntity<String> response = restTemplate.postForEntity(
      config.getBaseUrl() + "/api_unauth/keycloak/token",
      Map.of(
        "username",
        config.getUsername(),
        "password",
        config.getPassword()
      ),
      String.class
    );
    if (response.getBody() == null || response.getStatusCode().isError()) {
      throw new RuntimeException("Failed to get access token");
    }
    this.token = response.getBody();
    return this.token;
  }
}

