package software.uncharted.crawlimporter;

import java.util.Collection;
import java.util.Collections;
import java.util.List;
import java.util.Map;
import java.util.concurrent.atomic.AtomicLong;
import lombok.extern.slf4j.Slf4j;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.CommandLineRunner;
import org.springframework.boot.SpringApplication;
import org.springframework.boot.autoconfigure.SpringBootApplication;
import org.springframework.http.ResponseEntity;
import org.springframework.web.client.RestTemplate;
import software.uncharted.crawlimporter.configuration.CrawlImporterConfig;
import software.uncharted.crawlimporter.model.CrawlElement;
import software.uncharted.crawlimporter.model.CrawlFileElement;
import software.uncharted.crawlimporter.model.UploadedCrawlFile;
import software.uncharted.crawlimporter.service.CrawlFileService;
import software.uncharted.crawlimporter.service.CrawlImporterProjectService;
import software.uncharted.crawlimporter.service.CrawlMetadataService;

@SpringBootApplication
@Slf4j
public class CrawlImporterApplication implements CommandLineRunner {

  @Autowired
  private CrawlImporterConfig config;

  @Autowired
  private CrawlImporterProjectService crawlImporterProjectService;

  @Autowired
  private CrawlFileService crawlFileService;

  @Autowired
  private CrawlMetadataService crawlMetadataService;

  @Autowired
  private RestTemplate atlantisRestTemplate;

  // PipelineTask constants
  // modules/common/src/main/java/software/uncharted/pipeline/common/PipelineTask.java
  private static final List<String> DEFAULT_TASKS = List.of(
    "PROCESS_RAWTEXT",
    "PROCESS_FIRST_PAGE_IMAGE"
  );

  public static void main(String[] args) {
    SpringApplication.run(CrawlImporterApplication.class, args);
  }

  @Override
  public void run(String... args) {
    try {
      if (
        config.getRootCrawlPath() == null || config.getRootCrawlPath().isEmpty()
      ) {
        log.error(
          "Root crawl path is not set.  Ensure CRAWL_IMPORTER_ROOT_CRAWL_PATH is set to the local path to the crawl data"
        );
        System.exit(1);
      }

      // Create the project if needed
      if (!crawlImporterProjectService.projectExists(config.getProjectId())) {
        crawlImporterProjectService.createProject();
      }

      List<CrawlElement> elements = crawlMetadataService.load();
      if (elements.isEmpty()) {
        log.warn("No crawl elements found in metadata file");
        return;
      }
      log.info("Found {} crawl elements", elements.size());

      if (config.getShuffleFiles()) {
        log.info("Shuffling files to process");
        Collections.shuffle(elements);
      }

      final AtomicLong filesToProcess = new AtomicLong(
        elements
          .stream()
          .map(CrawlElement::getFiles)
          .flatMap(Collection::stream)
          .filter(f -> f.fileExists(config.getRootCrawlPath()))
          .count()
      );
      log.info(
        "Found {} files to process in {} elements",
        filesToProcess.get(),
        elements.size()
      );
      if (filesToProcess.get() < elements.size()) {
        final long missingFiles = elements.size() - filesToProcess.get();
        log.warn(
          "Found {} elements with missing files.  These will not be processed.",
          missingFiles
        );
      }

      final AtomicLong processed = new AtomicLong(0L);
      final AtomicLong avgMillisecondsPerFile = new AtomicLong(0L);
      elements
        .parallelStream()
        .forEach(element -> {
          final long startTime = System.currentTimeMillis();
          final CrawlFileElement crawlFileElement = element
            .getFiles()
            .stream()
            .filter(f -> f.fileExists(config.getRootCrawlPath()))
            .findFirst()
            .orElse(null);
          if (crawlFileElement == null) {
            log.debug("No file found for element: {}", element);
            return;
          }
          final UploadedCrawlFile uploadedCrawlFile =
            crawlFileService.uploadFileElement(crawlFileElement);

          var fileUpload = Map.of(
            "projectId",
            config.getProjectId(),
            "objectKey",
            uploadedCrawlFile.getKey(),
            "filename",
            uploadedCrawlFile.getFilename(),
            "fileSizeBytes",
            uploadedCrawlFile.getSizeInBytes(),
            "sha256Hash",
            uploadedCrawlFile.getSha256Hash(),
            "sync",
            true,
            "providedExtractions",
            element.toProvidedExtractions()
          );

          // FileImportRequest
          // modules/server/src/main/java/software/uncharted/pantera/model/fileupload/FileImportRequest.java
          var fileImportRequest = Map.of(
            "file",
            fileUpload,
            "tasks",
            DEFAULT_TASKS
          );

          final ResponseEntity<String> responseEntity =
            atlantisRestTemplate.postForEntity(
              "/api/document/import",
              fileImportRequest,
              String.class
            );
          if (responseEntity.getStatusCode().is2xxSuccessful()) {
            log.info(
              "Successfully uploaded file: {} to project: {}",
              uploadedCrawlFile.getFilename(),
              config.getProjectId()
            );
            processed.incrementAndGet();
          } else {
            log.error(
              "Failed to upload file: {} to project: {}. Response: {}",
              uploadedCrawlFile.getFilename(),
              config.getProjectId(),
              responseEntity.getBody()
            );
          }
          final long endTime = System.currentTimeMillis();
          final long elapsedTime = endTime - startTime;
          if (avgMillisecondsPerFile.get() == 0L) {
            avgMillisecondsPerFile.set(elapsedTime);
          } else {
            avgMillisecondsPerFile.set(
              (avgMillisecondsPerFile.get() + elapsedTime) / 2
            );
          }
          long remainingFiles = filesToProcess.get() - processed.get();
          long remainingTime =
            (remainingFiles * avgMillisecondsPerFile.get()) / 1000;
          int percentComplete = (int) ((remainingFiles * 100) /
            filesToProcess.get());
          log.info(
            "Processed {} files. {}% complete. Estimated time remaining: {} seconds",
            processed.get(),
            percentComplete,
            remainingTime
          );
        });
    } catch (Exception e) {
      log.error("Error during crawl import process", e);
      System.exit(1);
    } finally {
      log.info("Crawl import process completed");
      System.exit(0);
    }
  }
}

