    props.facets.pendingNumericFacets,
    props.facets.pendingCategoricalFacets,
  ],
  async () => {
    pendingFacetRowCount.value =
      await props.facets.getPendingFacetResultCount();
  },
);

watch(
  () => fileUploadStatuses.value,
  () => {
    updateFileStatuses();
