export default function useFacetMetadata(isDocumentMode: boolean = false) {
  const projectStore = useProjectStore();

  /**
   * A map from the facet ID (the property descriptor key it represents) to the display name, icon, and expansion state of that
   * property descriptor.
   */
  const facetMetadata = ref<{ [propertyDescriptorKey: string]: FacetMetadata }>(
    {},
  );

  /**
   * Ordered list of facet keys representing the user's preferred display order
   */
  const facetOrder = ref<string[]>([]);

  /**
   * Get the localStorage key for storing facet order
   */
  const getStorageKey = () => {
    const mode = isDocumentMode ? 'document' : 'regular';
    const projectId = projectStore.project?.id || 'default';
    return `facetOrder_${mode}_${projectId}`;
  };

  /**
   * Load facet order from localStorage
   */
  const loadFacetOrder = (): string[] => {
    try {
      const stored = localStorage.getItem(getStorageKey());
      return stored ? JSON.parse(stored) : [];
    } catch {
      return [];
    }
  };

  /**
   * Save facet order to localStorage
   */
  const saveFacetOrder = (order: string[]) => {
    try {
      localStorage.setItem(getStorageKey(), JSON.stringify(order));
      facetOrder.value = [...order];
    } catch (error) {
      Logger.warn('Failed to save facet order to localStorage:', error);
    }
  };

  /**
   * Set the facet display order
   */
  const setFacetOrder = (newOrder: string[]) => {
    saveFacetOrder(newOrder);
  };

  /**
   * Check if a facet order key represents a group
   */
  const isGroupKey = (key: string): boolean => key.startsWith('group:');

  /**
   * Get all facet keys that belong to a specific group
   */
  const getGroupFacets = (groupName: string): string[] =>
    Object.entries(facetMetadata.value)
      .filter(([, metadata]) => metadata.groupName === groupName)
      .map(([facetKey]) => facetKey);

  /**
   * Get a structured representation of facets organized by groups
   */
  const getGroupedFacetStructure = () => {
    const ungrouped: string[] = [];
    const groups: { [groupName: string]: string[] } = {};

    Object.entries(facetMetadata.value).forEach(([facetKey, metadata]) => {
      if (metadata.groupName) {
        if (!groups[metadata.groupName]) {
          groups[metadata.groupName] = [];
        }
        groups[metadata.groupName].push(facetKey);
      } else {
        ungrouped.push(facetKey);
      }
    });

    return { ungrouped, groups };
  };

  /**
   * Get the current facet order, with intelligent defaults when no custom order exists
   */
  const getFacetOrder = (facetData?: {
    [facetKey: string]: { uniqueValueCount: number };
  }): readonly string[] => {
    const { ungrouped, groups } = getGroupedFacetStructure();

    // For document mode, always apply singleton ordering even without facetData
    if (isDocumentMode && !facetData) {
      // Use saved order if available, but still apply singleton logic for document mode
      // We'll need to wait for facetData to do proper singleton sorting
      if (facetOrder.value.length > 0) {
        const existingFacetKeys = Object.keys(facetMetadata.value);
        const filteredOrder = facetOrder.value.filter(key => {
          if (isGroupKey(key)) {
            const groupName = key.substring(6);
            return groups[groupName] && groups[groupName].length > 0;
          }
          return (
            existingFacetKeys.includes(key) &&
            !facetMetadata.value[key]?.groupName
          );
        });

        // Add new ungrouped facets and groups
        const newUngroupedKeys = ungrouped.filter(
          key => !facetOrder.value.includes(key),
        );
        const newGroupKeys = Object.keys(groups)
          .map(name => `group:${name}`)
          .filter(groupKey => !facetOrder.value.includes(groupKey));

        return [...filteredOrder, ...newUngroupedKeys, ...newGroupKeys];
      }

      // No saved order - create basic order, will be refined when facetData arrives
      const setFacets = ungrouped.filter(
        key => key === 'set' || key === 'sets',
      );
      const provenanceFacets = ungrouped.filter(
        key => key.includes('provenance') || key.includes('references'),
      );
      const regularUngrouped = ungrouped.filter(
        key =>
          key !== 'set' &&
          key !== 'sets' &&
          !key.includes('provenance') &&
          !key.includes('references'),
      );

      const groupKeys = Object.keys(groups)
        .sort()
        .map(name => `group:${name}`);

      return [
        ...setFacets,
        ...regularUngrouped, // Will be sorted when facetData arrives
        ...groupKeys,
        ...provenanceFacets,
      ];
    }

    if (facetOrder.value.length > 0) {
      // Filter out any keys/groups that no longer exist and add any new ones
      const existingFacetKeys = Object.keys(facetMetadata.value);

      const filteredOrder = facetOrder.value.filter(key => {
        if (isGroupKey(key)) {
          const groupName = key.substring(6); // Remove 'group:' prefix
          return groups[groupName] && groups[groupName].length > 0;
        }
        return (
          existingFacetKeys.includes(key) &&
          !facetMetadata.value[key]?.groupName
        );
      });

      // Add new ungrouped facets and groups
      const newUngroupedKeys = ungrouped.filter(
        key => !facetOrder.value.includes(key),
      );
      const newGroupKeys = Object.keys(groups)
        .map(name => `group:${name}`)
        .filter(groupKey => !facetOrder.value.includes(groupKey));

      return [...filteredOrder, ...newUngroupedKeys, ...newGroupKeys];
    }

    // No saved order - create smart default with groups
    const setFacets = ungrouped.filter(key => key === 'set' || key === 'sets');
    const provenanceFacets = ungrouped.filter(
      key => key.includes('provenance') || key.includes('references'),
    );
    const regularUngrouped = ungrouped.filter(
      key =>
        key !== 'set' &&
        key !== 'sets' &&
        !key.includes('provenance') &&
        !key.includes('references'),
    );

    const sortByDisplayName = (a: string, b: string) => {
      const aName = facetMetadata.value[a]?.displayName || a;
      const bName = facetMetadata.value[b]?.displayName || b;
      return aName.localeCompare(bName);
    };

    if (!facetData) {
      regularUngrouped.sort(sortByDisplayName);
      provenanceFacets.sort(sortByDisplayName);

      const groupKeys = Object.keys(groups)
        .sort()
        .map(name => `group:${name}`);

      return [
        ...setFacets,
        ...regularUngrouped,
        ...groupKeys,
        ...provenanceFacets,
      ];
    }

    // Apply intelligent default with facet data
    const singletons: string[] = [];
    const others: string[] = [];

    regularUngrouped.forEach(key => {
      const uniqueCount = facetData[key]?.uniqueValueCount ?? 0;
      if (uniqueCount === 1) {
        singletons.push(key);
      } else {
        others.push(key);
      }
    });

    singletons.sort(sortByDisplayName);
    others.sort(sortByDisplayName);
    provenanceFacets.sort(sortByDisplayName);

    const groupKeys = Object.keys(groups)
      .sort()
      .map(name => `group:${name}`);

    const result = [
      ...setFacets,
      ...singletons,
      ...others,
      ...groupKeys,
      ...provenanceFacets,
    ];
    return result;
  };
  /**
   * Fetch the facet data for all property descriptors that are tagged "FACET"
   */
  watch(
    () => projectStore.project,
    async _project => {
      if (_project === undefined) return;
      nextTick(() => {
        // Get the facet descriptors based on the mode (document or not)
        const propertyTag = isDocumentMode
          ? PropertyTag.DocumentFacet
          : PropertyTag.Facet;
        const facetDescriptors =
          projectStore.getPropertyDescriptorsWithTag(propertyTag);

        if (facetDescriptors.length === 0) {
          facetMetadata.value = {};
          facetOrder.value = [];
          return;
        }

        // Save facet descriptor metadata in lookup map
        const newFacetMetadata: {
          [propertyDescriptorKey: string]: FacetMetadata;
        } = {};
        facetDescriptors.forEach(({ key, name, icon, tags, groupName }) => {
          newFacetMetadata[key] = {
            displayName: name,
            icon,
            isExpanded: true,
            isExtractable: tags.includes(PropertyTag.Enrichable),
            groupName,
            tags,
          };
        });
        facetMetadata.value = newFacetMetadata;

        // Load saved facet order from localStorage
        facetOrder.value = loadFacetOrder();
      });
    },
    { immediate: true },
  );

  /**
   * Expand an order array containing group keys into individual facets
   * This is used for rendering where we need the actual facet keys
   */
  const expandGroupsInOrder = (order: readonly string[]): string[] => {
    const expanded: string[] = [];

    order.forEach(key => {
      if (isGroupKey(key)) {
        const groupName = key.substring(6); // Remove 'group:' prefix
        const groupFacets = getGroupFacets(groupName);
        expanded.push(...groupFacets);
      } else {
        expanded.push(key);
      }
    });

    return expanded;
  };

  return {
    facetMetadata,
    setFacetOrder,
    getFacetOrder,
    isGroupKey,
    getGroupFacets,
    getGroupedFacetStructure,
    expandGroupsInOrder,
  };
}

