// Helper function to create a facet item
const createFacetItem = (
  facetKey: string,
  aggregateProperties: any,
): FacetItem => {
  const displayName = props.facetMetadata[facetKey]?.displayName ?? facetKey;
  const {
    sortDirection,
    visibleRowCount,
    queryString: bucketQueryString,
  } = props.facets.getCategoricalFacetDisplayState(facetKey);

  return {
    facetKey,
    displayName,
    isExpanded: props.facets.isFacetExpanded(facetKey),
    aggregateProperties,
    selectedBucketKeys: props.facets.selectedFacets.value[facetKey] ?? [],
    sortDirection,
    visibleRowCount,
    queryString: bucketQueryString,
    isHistogramFacet: false,
    icon: props.facetMetadata[facetKey]?.icon,
    isExtractable: props.facetMetadata[facetKey].isExtractable ?? false,
  };
};

// Create structured render items (groups and individual facets)
const renderItems = computed((): RenderItem[] => {
  if (!props.document || !props.document?.propertyMap) return [];

  const facetOrder =
    props.getFacetOrder?.(props.facets.facetData.value?.facets) || [];
  const facetData = props.facets.facetData.value?.facets ?? {};
  const visibleFacetKeys = props.facets.visibleFacetKeys.value;
  const items: RenderItem[] = [];

  facetOrder.forEach(orderKey => {
    if (isGroupKey(orderKey)) {
      // Handle group - only show if group is selected
      if (!visibleFacetKeys.includes(orderKey)) return;

      const groupName = orderKey.substring(6); // Remove 'group:' prefix
      // Get group facets in the order they appear in visibleFacetKeys (preserving FacetSelector order)
      const allGroupFacets = getGroupFacets(groupName);
      const groupFacets = visibleFacetKeys.filter(key =>
        allGroupFacets.includes(key),
      );
      const facetItems = groupFacets
        .map(facetKey => {
          // Only include facets that are selected and available
          if (!visibleFacetKeys.includes(facetKey)) return null;

          const aggregateProperties = facetData[facetKey];
          const metadata = props.facetMetadata[facetKey];
          const isDerived = metadata?.tags?.includes(PropertyTag.Derived);

          // Show derived facets even without data, others only with data
          return aggregateProperties || isDerived
            ? createFacetItem(
                facetKey,
                aggregateProperties || createEmptyAggregateProperties(),
              )
            : null;
        })
        .filter((facet): facet is FacetItem => facet !== null);

      if (facetItems.length > 0) {
        items.push({
          type: 'group',
          groupName,
          facets: facetItems,
          isExpanded: isGroupExpanded(groupName),
        });
      }
    } else {
      // Handle individual facet - only show if selected
      if (!visibleFacetKeys.includes(orderKey)) return;

      const aggregateProperties = facetData[orderKey];
      const metadata = props.facetMetadata[orderKey];
      const isDerived = metadata?.tags?.includes(PropertyTag.Derived);

      // Show derived facets even without data, others only with data
      if (aggregateProperties || isDerived) {
        const facetItem = createFacetItem(
          orderKey,
          aggregateProperties || createEmptyAggregateProperties(),
        );
        items.push({
          type: 'facet',
          facet: facetItem,
        });
      }
    }
  });

  return items;
});

