diff --git a/modules/client/src/components/facets/FacetHeaderGroup.vue b/modules/client/src/components/facets/FacetHeaderGroup.vue
new file mode 100644
index 00000000..051917d9
--- /dev/null
+++ b/modules/client/src/components/facets/FacetHeaderGroup.vue
@@ -0,0 +1,55 @@
+<template>
+  <div class="facet-group">
+    <FacetHeader
+      :facet-display-name="groupName"
+      :is-expanded="isExpanded"
+      @set-is-expanded="newValue => emit('set-is-expanded', newValue)"
+    >
+      <template #actions>
+        <span class="group-facet-count">{{ facetCount }}</span>
+      </template>
+    </FacetHeader>
+
+    <div v-if="isExpanded" class="facet-group-content">
+      <slot />
+    </div>
+  </div>
+</template>
+
+<script setup lang="ts">
+import FacetHeader from './FacetHeader.vue';
+
+defineProps<{
+  groupName: string;
+  isExpanded: boolean;
+  facetCount: number;
+}>();
+
+const emit = defineEmits<{
+  (e: 'set-is-expanded', newValue: boolean): void;
+}>();
+</script>
+
+<style scoped>
+.facet-group {
+  border: 1px solid var(--surface-border);
+  border-radius: var(--border-radius);
+  background: var(--surface-card);
+  margin-bottom: var(--gap-2);
+}
+
+.facet-group-content {
+  padding: 0 var(--gap-2) var(--gap-2) var(--gap-2);
+  border-top: 1px solid var(--surface-border);
+  background: var(--surface-ground);
+}
+
+.group-facet-count {
+  color: var(--text-color-secondary);
+  font-size: var(--text-size-small);
+  font-weight: 400;
+  background: var(--surface-100);
+  padding: var(--gap-0-5) var(--gap-1);
+  border-radius: var(--border-radius-small);
+}
+</style>

