 </template>

 <script setup lang="ts">
 import { FontAwesomeIcon } from '@fortawesome/vue-fontawesome';
 import Button from 'primevue/button';
-import Dropdown from 'primevue/dropdown';
-import { computed, ref, watch, ComponentPublicInstance } from 'vue';
-import useProjectStore from '@/stores/ProjectStore';
-import { FileUploadStatusEnum, PropertyTag } from '@/types/Types';
-import { getPropertyDescriptorsByTag } from '@/util/Document';
-import SearchBar from '../SearchBar.vue';
-import CircleProgress from '../CircleProgress.vue';
+import { ref, ComponentPublicInstance } from 'vue';

 const props = defineProps<{
-  facetKey: string;
-  isExpanded: boolean;
   facetDisplayName: string;
-  facetIcon: string | undefined;
-  totalBucketCount: string | undefined;
-  documentCountWithFacet: number | undefined;
-  sortDirection: number;
-  visibleRowCount: number;
-  queryString: string;
-  isExtractable: boolean;
-  isNumeric: boolean;
-  status?: FileUploadStatusEnum;
-  isDocumentMode?: boolean;
+  isExpanded: boolean;
 }>();

-interface FacetSortOption {
-  key: string;
-  title: string;
-  sortDirection: number;
-  visibleRowCount: number;
-}
-
 const emit = defineEmits<{
   (e: 'set-is-expanded', newValue: boolean): void;
-  (e: 'set-option', newValue: FacetSortOption): void;
-  (e: 'update-query-string', newValue: string): void;
-  (e: 'extract-facet'): void;
 }>();

-const isBucketSearchExpanded = ref(false);
-const topXDropdown = ref<InstanceType<typeof Dropdown> | null>(null);
-const totalDocumentsInSearch = ref<number>(0);
-
-const projectStore = useProjectStore();
-
-const shouldShowSparkles = computed(() => {
-  const descriptors = getPropertyDescriptorsByTag(PropertyTag.Derived);
-  return descriptors.some(descriptor => descriptor.key === props.facetKey);
-});
-
-const canExtractMore = computed(
-  () =>
-    props.isExtractable &&
-    !props.isDocumentMode &&
-    props.documentCountWithFacet &&
-    props.documentCountWithFacet < totalDocumentsInSearch.value,
-);
-
-const hasMultipleOptions = computed(
-  () =>
-    !props.isNumeric &&
-    props.totalBucketCount &&
-    parseInt(props.totalBucketCount, 10) > 5,
-);
-
-const shouldShowDropdown = computed(
-  () => props.isNumeric || canExtractMore.value || hasMultipleOptions.value,
-);
-
-const tooltipText = computed(() => {
-  if (!canExtractMore.value) {
-    return '';
-  }
-  return `${props.facetDisplayName} were extracted from ${props.documentCountWithFacet} of ${totalDocumentsInSearch.value} documents.`;
-});
-
-const options = computed<FacetSortOption[]>(() => {
-  const categoricalOptions = [
-    { key: 'top-5', title: 'Top 5', sortDirection: -1, visibleRowCount: 5 },
-    { key: 'top-20', title: 'Top 20', sortDirection: -1, visibleRowCount: 20 },
-    { key: 'top-50', title: 'Top 50', sortDirection: -1, visibleRowCount: 50 },
-    {
-      key: 'bottom-5',
-      title: 'Bottom 5',
-      sortDirection: 1,
-      visibleRowCount: 5,
-    },
-    {
-      key: 'bottom-20',
-      title: 'Bottom 20',
-      sortDirection: 1,
-      visibleRowCount: 20,
-    },
-    {
-      key: 'bottom-50',
-      title: 'Bottom 50',
-      sortDirection: 1,
-      visibleRowCount: 50,
-    },
-    {
-      key: 'search-all',
-      title: 'Search all',
-      sortDirection: -1,
-      visibleRowCount: 50,
-    },
-  ];
-
-  const baseOptions = props.isNumeric ? [] : categoricalOptions;
-
-  if (canExtractMore.value) {
-    baseOptions.push({
-      key: 'extract-more',
-      title: 'Extract more...',
-      sortDirection: -1,
-      visibleRowCount: 50,
-    });
-  }
-
-  return baseOptions;
-});
-
-const selectedOption = computed(
-  () =>
-    options.value.find(
-      option =>
-        option.sortDirection === props.sortDirection &&
-        option.visibleRowCount === props.visibleRowCount,
-    ) ?? options.value[0],
-);
-
-const selectedDropdownOption = ref<FacetSortOption>(selectedOption.value);
-
-const shouldShowCount = computed(() => {
-  const bucketCount = props.totalBucketCount
-    ? parseInt(props.totalBucketCount, 10)
-    : 0;
-  const selectedKey = selectedDropdownOption.value.key;
-  const hideCountKeys = ['search-all', 'extract-more', 'show-all'];
-
-  return (
-    bucketCount > selectedOption.value.visibleRowCount &&
-    !hideCountKeys.includes(selectedKey)
-  );
-});
-
-const setTotalDocumentsInSearch = () => {
-  totalDocumentsInSearch.value = projectStore.totalDocumentCount ?? 0;
-};
-
 const toggleButtonRef = ref<ComponentPublicInstance | null>(null);

 const toggleExpansion = () => {
@@ -290,49 +64,20 @@ const toggleExpansion = () => {
     toggleButtonRef.value.$el.blur();
   }
 };
-
-const onDropdownUpdate = (newValue: FacetSortOption) => {
-  if (newValue.key === 'search-all') {
-    isBucketSearchExpanded.value = true;
-    selectedDropdownOption.value = newValue;
-    if (topXDropdown.value) topXDropdown.value.hide();
-  } else if (newValue.key === 'extract-more') {
-    if (topXDropdown.value) topXDropdown.value.hide();
-    emit('extract-facet');
-  } else {
-    isBucketSearchExpanded.value = false;
-    selectedDropdownOption.value = newValue;
-    emit('set-option', newValue);
-  }
-};
-
-// Keep in sync with selectedOption from props
-watch(selectedOption, val => {
-  selectedDropdownOption.value = val;
-});
-
-// Watch for project store changes to update document count
-watch(
-  [projectStore],
-  () => {
-    setTotalDocumentsInSearch();
-  },
-  { immediate: true },
-);
 </script>

