diff --git a/modules/client/src/components/facets/FacetHeaderProperty.vue b/modules/client/src/components/facets/FacetHeaderProperty.vue
new file mode 100644
index 00000000..3ac77925
--- /dev/null
+++ b/modules/client/src/components/facets/FacetHeaderProperty.vue
@@ -0,0 +1,431 @@
+<template>
+  <FacetHeader
+    :facet-display-name="facetDisplayName"
+    :is-expanded="isExpanded"
+    @set-is-expanded="newValue => emit('set-is-expanded', newValue)"
+  >
+    <template #title-suffix>
+      <i
+        v-if="status === FileUploadStatusEnum.InProgress"
+        v-tooltip.right="tooltipText"
+        class="pi pi-sparkles pulse"
+        :class="{ 'extract-more': canExtractMore }"
+        @click.stop="emit('extract-facet')"
+        @keydown.enter.stop="emit('extract-facet')"
+      />
+      <i
+        v-else-if="shouldShowSparkles"
+        v-tooltip.right="tooltipText"
+        class="pi pi-sparkles"
+        :class="{ 'extract-more': canExtractMore }"
+        @click.stop="emit('extract-facet')"
+        @keydown.enter.stop="emit('extract-facet')"
+      />
+      <FontAwesomeIcon
+        v-if="facetIcon"
+        :icon="facetIcon"
+        fixed-width
+        class="title-icon"
+      />
+    </template>
+
+    <template #actions>
+      <template v-if="shouldShowDropdown">
+        <Dropdown
+          v-if="isExpanded"
+          :model-value="selectedDropdownOption"
+          @update:model-value="onDropdownUpdate"
+          :options="options"
+          option-label="title"
+          class="dropdown-display-how-many"
+          panelClass="small-dropdown-panel"
+          ref="topXDropdown"
+        >
+          <template #value>
+            <span class="dropdown-total-count">
+              <!-- For numeric facets, show total count as the value -->
+              <template v-if="props.isNumeric">
+                {{ `${props.totalBucketCount}${canExtractMore ? '+' : ''}` }}
+              </template>
+              <!-- For categorical facets, show selected option -->
+              <template v-else>
+                {{ selectedDropdownOption.title }}
+                <span v-if="shouldShowCount">
+                  of
+                  {{ `${props.totalBucketCount}${canExtractMore ? '+' : ''}` }}
+                </span>
+                <span v-else>({{ props.totalBucketCount }} total)</span>
+              </template>
+            </span>
+          </template>
+          <template #option="{ option }">
+            <span
+              v-if="option.key === 'extract-more'"
+              class="extract-more-option"
+            >
+              <CircleProgress
+                v-tooltip.bottom="tooltipText"
+                :percentage="
+                  ((props.documentCountWithFacet || 0) /
+                    totalDocumentsInSearch) *
+                  100
+                "
+                pie-chart
+                class="mr-1"
+              />{{ option.title }}
+            </span>
+            <span
+              v-else
+              class="dropdown-option"
+              :class="{ 'aligned-with-icon': canExtractMore }"
+            >
+              {{ option.title }}
+            </span>
+          </template>
+        </Dropdown>
+
+        <span v-else class="dropdown-total-count pr-2">
+          {{ `${props.totalBucketCount}${canExtractMore ? '+' : ''}` }}
+        </span>
+      </template>
+    </template>
+
+    <template #footer>
+      <div
+        v-if="isBucketSearchExpanded && isExpanded"
+        class="search-bar-container"
+      >
+        <SearchBar
+          :query-string="queryString"
+          search-mode="keyword"
+          :should-show-clear-button="!!queryString"
+          :should-search-on-keyup="false"
+          :should-show-search-mode-toggle="false"
+          placeholder-text="Search facets"
+          size="small"
+          class="search-bar"
+          @update:query-string="
+            newValue => emit('update-query-string', newValue)
+          "
+        />
+      </div>
+    </template>
+  </FacetHeader>
+</template>
+
+<script setup lang="ts">
+import { FontAwesomeIcon } from '@fortawesome/vue-fontawesome';
+import Dropdown from 'primevue/dropdown';
+import { computed, ref, watch } from 'vue';
+import useProjectStore from '@/stores/ProjectStore';
+import { FileUploadStatusEnum, PropertyTag } from '@/types/Types';
+import { getPropertyDescriptorsByTag } from '@/util/Document';
+import SearchBar from '../SearchBar.vue';
+import CircleProgress from '../CircleProgress.vue';
+import FacetHeader from './FacetHeader.vue';
+
+const props = defineProps<{
+  facetKey: string;
+  isExpanded: boolean;
+  facetDisplayName: string;
+  facetIcon?: string;
+  totalBucketCount?: string;
+  documentCountWithFacet?: number;
+  sortDirection: number;
+  visibleRowCount: number;
+  queryString: string;
+  isExtractable: boolean;
+  isNumeric: boolean;
+  status?: FileUploadStatusEnum;
+  isDocumentMode?: boolean;
+}>();
+
+interface FacetSortOption {
+  key: string;
+  title: string;
+  sortDirection: number;
+  visibleRowCount: number;
+}
+
+const emit = defineEmits<{
+  (e: 'set-is-expanded', newValue: boolean): void;
+  (e: 'set-option', newValue: FacetSortOption): void;
+  (e: 'update-query-string', newValue: string): void;
+  (e: 'extract-facet'): void;
+}>();
+
+const isBucketSearchExpanded = ref(false);
+const topXDropdown = ref<InstanceType<typeof Dropdown> | null>(null);
+const totalDocumentsInSearch = ref<number>(0);
+
+const projectStore = useProjectStore();
+
+const shouldShowSparkles = computed(() => {
+  const descriptors = getPropertyDescriptorsByTag(PropertyTag.Derived);
+  return descriptors.some(descriptor => descriptor.key === props.facetKey);
+});
+
+const canExtractMore = computed(
+  () =>
+    props.isExtractable &&
+    !props.isDocumentMode &&
+    props.documentCountWithFacet &&
+    props.documentCountWithFacet < totalDocumentsInSearch.value,
+);
+
+const hasMultipleOptions = computed(
+  () =>
+    !props.isNumeric &&
+    props.totalBucketCount &&
+    parseInt(props.totalBucketCount, 10) > 5,
+);
+
+const shouldShowDropdown = computed(
+  () => props.isNumeric || canExtractMore.value || hasMultipleOptions.value,
+);
+
+const tooltipText = computed(() => {
+  if (!canExtractMore.value) {
+    return '';
+  }
+  return `${props.facetDisplayName} were extracted from ${props.documentCountWithFacet} of ${totalDocumentsInSearch.value} documents.`;
+});
+
+const options = computed<FacetSortOption[]>(() => {
+  const categoricalOptions = [
+    { key: 'top-5', title: 'Top 5', sortDirection: -1, visibleRowCount: 5 },
+    { key: 'top-20', title: 'Top 20', sortDirection: -1, visibleRowCount: 20 },
+    { key: 'top-50', title: 'Top 50', sortDirection: -1, visibleRowCount: 50 },
+    {
+      key: 'bottom-5',
+      title: 'Bottom 5',
+      sortDirection: 1,
+      visibleRowCount: 5,
+    },
+    {
+      key: 'bottom-20',
+      title: 'Bottom 20',
+      sortDirection: 1,
+      visibleRowCount: 20,
+    },
+    {
+      key: 'bottom-50',
+      title: 'Bottom 50',
+      sortDirection: 1,
+      visibleRowCount: 50,
+    },
+    {
+      key: 'search-all',
+      title: 'Search all',
+      sortDirection: -1,
+      visibleRowCount: 50,
+    },
+  ];
+
+  const baseOptions = props.isNumeric ? [] : categoricalOptions;
+
+  if (canExtractMore.value) {
+    baseOptions.push({
+      key: 'extract-more',
+      title: 'Extract more...',
+      sortDirection: -1,
+      visibleRowCount: 50,
+    });
+  }
+
+  return baseOptions;
+});
+
+const selectedOption = computed(
+  () =>
+    options.value.find(
+      option =>
+        option.sortDirection === props.sortDirection &&
+        option.visibleRowCount === props.visibleRowCount,
+    ) ?? options.value[0],
+);
+
+const selectedDropdownOption = ref<FacetSortOption>(selectedOption.value);
+
+const shouldShowCount = computed(() => {
+  const bucketCount = props.totalBucketCount
+    ? parseInt(props.totalBucketCount, 10)
+    : 0;
+  const selectedKey = selectedDropdownOption.value.key;
+  const hideCountKeys = ['search-all', 'extract-more', 'show-all'];
+
+  return (
+    bucketCount > selectedOption.value.visibleRowCount &&
+    !hideCountKeys.includes(selectedKey)
+  );
+});
+
+const setTotalDocumentsInSearch = () => {
+  totalDocumentsInSearch.value = projectStore.totalDocumentCount ?? 0;
+};
+
+const onDropdownUpdate = (newValue: FacetSortOption) => {
+  if (newValue.key === 'search-all') {
+    isBucketSearchExpanded.value = true;
+    selectedDropdownOption.value = newValue;
+    if (topXDropdown.value) topXDropdown.value.hide();
+  } else if (newValue.key === 'extract-more') {
+    if (topXDropdown.value) topXDropdown.value.hide();
+    emit('extract-facet');
+  } else {
+    isBucketSearchExpanded.value = false;
+    selectedDropdownOption.value = newValue;
+    emit('set-option', newValue);
+  }
+};
+
+// Keep in sync with selectedOption from props
+watch(selectedOption, val => {
+  selectedDropdownOption.value = val;
+});
+
+// Watch for project store changes to update document count
+watch(
+  [projectStore],
+  () => {
+    setTotalDocumentsInSearch();
+  },
+  { immediate: true },
+);
+</script>
+
+<style scoped>
+.title-icon {
+  margin-left: var(--gap-1);
+  color: var(--surface-300);
+}
+
+.dropdown-display-how-many {
+  border-radius: var(--gap-1);
+  background-color: transparent;
+  border-color: transparent;
+  box-shadow: none;
+
+  &:hover {
+    background-color: var(--surface-100);
+  }
+}
+
+:deep(.p-dropdown-label) {
+  padding: var(--gap-1) var(--gap-2);
+  font-size: var(--text-size-small);
+}
+
+:deep(.p-dropdown-trigger) {
+  display: none;
+}
+
+.total-row-count {
+  color: #fff;
+}
+
+i.pi-sparkles {
+  color: var(--surface-200);
+  font-size: var(--text-size-small);
+  margin-left: var(--gap-1);
+  &.extract-more {
+    color: var(--primary-color);
+    &:hover {
+      color: #fff;
+    }
+  }
+}
+
+.extraction-info-text {
+  margin-bottom: 0;
+  color: var(--text-color-secondary);
+  font-size: var(--text-size-small);
+  line-height: 1.2;
+  display: flex;
+  align-items: center;
+  justify-content: space-between;
+}
+
+.extract-button {
+  border-radius: var(--border-radius-small);
+  background-color: transparent;
+  border: none;
+  color: var(--primary-color);
+  font-size: 10px;
+  font-weight: 400;
+  padding: var(--gap-1) var(--gap-1-5);
+}
+
+.extract-button:hover {
+  background-color: color-mix(in srgb, var(--surface-300) 50%, transparent);
+}
+
+.dropdown-total-count {
+  color: var(--text-color-secondary);
+  font-size: var(--text-size-small);
+  font-weight: 400;
+}
+
+.clickable-count {
+  cursor: pointer;
+  border-radius: var(--gap-1);
+  padding: var(--gap-1) var(--gap-2);
+  transition: background-color 0.15s ease;
+}
+
+.clickable-count:hover {
+  background-color: var(--surface-100);
+}
+
+.search-bar-container {
+  display: flex;
+  align-items: center;
+  width: 100%;
+  margin-top: var(--gap-0-5);
+}
+
+:deep(.search-input-container) {
+  padding-left: 0;
+}
+
+:deep(.icon-container) {
+  right: 8px;
+}
+
+.small-text {
+  white-space: nowrap;
+  overflow-x: hidden;
+  text-overflow: ellipsis;
+  color: var(--text-color-secondary);
+  flex: 1;
+  min-width: 0;
+  font-size: var(--text-size-small);
+}
+
+@keyframes pulse {
+  0% {
+    opacity: 1;
+  }
+  50% {
+    opacity: 0.6;
+  }
+  100% {
+    opacity: 1;
+  }
+}
+
+.pulse {
+  animation: pulse 1.2s infinite;
+}
+
+.extract-more-option {
+  display: flex;
+  align-items: center;
+  color: var(--primary-color);
+}
+
+/* Align dropdown options with icon IF extract more is present as an option */
+.dropdown-option.aligned-with-icon {
+  margin-left: 1rem;
+}
+</style>

