  if (internalOptions.value.length > 0) {
    return;
  }

  const processedOptions: FacetOption[] = [];
  const processedValues = new Set<string>();

  props.options.forEach(option => {
    if (processedValues.has(option.value)) return;

    if (props.isGroupKey?.(option.value)) {
      const groupName = option.value.substring(6);
      const groupFacets = props.getGroupFacets?.(groupName) ?? [];

      const children: FacetOption[] = groupFacets
        .map(facetKey => {
          const facetOption = props.options.find(opt => opt.value === facetKey);
          const metadata = props.facetMetadata?.[facetKey];
          const isDerived = metadata?.tags?.includes('DERIVED' as any);

          if (facetOption) {
            processedValues.add(facetKey);
            return {
              ...facetOption,
              selected: props.modelValue.includes(facetKey),
            };
          }
          if (isDerived && metadata) {
            processedValues.add(facetKey);
            return {
              label: metadata.displayName,
              value: facetKey,
              selected: props.modelValue.includes(facetKey),
            };
          }
          return null;
        })
        .filter((child): child is FacetOption => child !== null);

      if (children.length > 0) {
        processedOptions.push({
          ...option,
          isGroup: true,
          groupName,
          children,
          selected: props.modelValue.includes(option.value),
        });
      }
      processedValues.add(option.value);
    } else if (!processedValues.has(option.value)) {
      processedOptions.push({
        ...option,
        selected: props.modelValue.includes(option.value),
      });
      processedValues.add(option.value);
    }
  });

  if (props.facetMetadata) {
    Object.entries(props.facetMetadata).forEach(([facetKey, metadata]) => {
      const isDerived = metadata.tags?.includes('DERIVED' as any);
      const hasGroup = metadata.groupName;

      if (isDerived && !processedValues.has(facetKey) && !hasGroup) {
        processedOptions.push({
          label: metadata.displayName,
          value: facetKey,
          selected: props.modelValue.includes(facetKey),
        });
        processedValues.add(facetKey);
      }
    });
  }

  const setFacets = processedOptions.filter(option => isSetFacet(option.value));
  const provenanceFacets = processedOptions.filter(option =>
    isProvenanceFacet(option.value),
  );

  const reorderableFacets = props.options
    .filter(option =>
      processedOptions.some(
        po => po.value === option.value && isReorderableFacet(option.value),
      ),
    )
    .map(option => processedOptions.find(po => po.value === option.value)!)
    .filter(Boolean);

  internalOptions.value = [
    ...setFacets,
    ...reorderableFacets,
    ...provenanceFacets,
  ];

  emitUpdate();
};

