import { computed, ComputedRef } from 'vue';
import { PropertyTag } from '@/types/Types';

export interface FacetItem {
  facetKey: string;
  displayName: string;
  isExpanded: boolean;
  aggregateProperties: any;
  selectedBucketKeys: string[];
  sortDirection: number;
  visibleRowCount: number;
  queryString: string;
  isHistogramFacet: boolean;
  icon?: string;
  isExtractable: boolean;
}

export interface GroupItem {
  type: 'group';
  groupName: string;
  facets: FacetItem[];
  isExpanded: boolean;
}

export interface IndividualFacetItem {
  type: 'facet';
  facet: FacetItem;
}

export type RenderItem = GroupItem | IndividualFacetItem;

interface FacetMetadata {
  displayName: string;
  icon?: string;
  isExtractable: boolean;
  groupName?: string;
  tags?: string[];
}

interface FacetState {
  getCategoricalFacetDisplayState: (facetKey: string) => {
    sortDirection: number;
    visibleRowCount: number;
    queryString: string;
  };
  isFacetExpanded: (facetKey: string) => boolean;
  isHistogramFacet?: (aggregateProperties: any) => boolean;
  selectedFacets?: { value: { [facetKey: string]: string[] } };
  pendingCategoricalFacets?: { value: { [facetKey: string]: string[] } };
  visibleFacetKeys: { value: string[] };
  facetData?: { value: { facets?: { [facetKey: string]: any } } };
}

interface UseFacetPanelRendererOptions {
  facetMetadata: { [facetKey: string]: FacetMetadata };
  facets: FacetState;
  getFacetOrder: (facetData?: any) => readonly string[];
  isGroupKey: (key: string) => boolean;
  getGroupFacets: (groupName: string) => string[];
  isDocumentMode?: boolean;
}

export default function useFacetPanelRenderer(options: UseFacetPanelRendererOptions) {
  const {
    facetMetadata,
    facets,
    getFacetOrder,
    isGroupKey,
    getGroupFacets,
    isDocumentMode = false,
  } = options;

  const createFacetItem = (facetKey: string, aggregateProperties: any): FacetItem => {
    const displayName = facetMetadata[facetKey]?.displayName ?? facetKey;
    const {
      sortDirection,
      visibleRowCount,
      queryString: bucketQueryString,
    } = facets.getCategoricalFacetDisplayState(facetKey);

    let selectedBucketKeys: string[];
    let isHistogramFacet: boolean;

    if (isDocumentMode) {
      selectedBucketKeys = facets.selectedFacets?.value[facetKey] ?? [];
      isHistogramFacet = false; // Document mode always uses false
    } else {
      selectedBucketKeys = facets.pendingCategoricalFacets?.value?.[facetKey] ?? [];
      isHistogramFacet = facets.isHistogramFacet?.(aggregateProperties) ?? false;
    }

    return {
      facetKey,
      displayName,
      isExpanded: facets.isFacetExpanded(facetKey),
      aggregateProperties,
      isHistogramFacet,
      selectedBucketKeys,
      sortDirection,
      visibleRowCount,
      queryString: bucketQueryString,
      icon: facetMetadata[facetKey]?.icon,
      isExtractable: facetMetadata[facetKey]?.isExtractable ?? false,
    };
  };

  const createEmptyAggregateProperties = (facetKey: string) => {
    if (isDocumentMode) {
      return {
        buckets: [],
        facetKey,
        uniqueValueCount: 0,
      };
    } else {
      return {
        properties: [],
        uniqueValueCount: 0,
        buckets: [],
      };
    }
  };

  const isGroupExpanded = (groupName: string): boolean => {
    const groupId = `group:${groupName}`;
    return facets.isFacetExpanded(groupId);
  };

  const shouldIncludeFacet = (facetKey: string, aggregateProperties: any): boolean => {
    const isDerived = facetMetadata[facetKey]?.tags?.includes(PropertyTag.Derived);

    // Show derived facets even without data, others only with data
    return aggregateProperties || isDerived;
  };

  const createRenderItems = (
    document?: any // For document mode validation
  ): ComputedRef<RenderItem[]> => {
    return computed(() => {
      // Early return for document mode if no document
      if (isDocumentMode && (!document || !document?.propertyMap)) {
        return [];
      }

      const facetDataForOrder = isDocumentMode 
        ? facets.facetData?.value?.facets
        : facets.facetData?.value?.facets;

      const facetOrder = getFacetOrder(facetDataForOrder);
      const facetData = facetDataForOrder ?? {};
      const visibleFacetKeys = facets.visibleFacetKeys.value;
      const items: RenderItem[] = [];

      facetOrder.forEach(orderKey => {
        if (isGroupKey(orderKey)) {
          if (!visibleFacetKeys.includes(orderKey)) return;

          const groupName = orderKey.substring(6);
          const allGroupFacets = getGroupFacets(groupName);
          const groupFacets = visibleFacetKeys.filter(key =>
            allGroupFacets.includes(key)
          );

          const facetItems = groupFacets
            .map(facetKey => {
              if (!visibleFacetKeys.includes(facetKey)) return null;

              const aggregateProperties = facetData[facetKey];

              if (shouldIncludeFacet(facetKey, aggregateProperties)) {
                const properties = aggregateProperties || createEmptyAggregateProperties(facetKey);
                return createFacetItem(facetKey, properties);
              }
              return null;
            })
            .filter((facet): facet is FacetItem => facet !== null);

          if (facetItems.length > 0) {
            // Sort within group: singletons first, then others (for document mode)
            if (isDocumentMode && facetDataForOrder) {
              facetItems.sort((a, b) => {
                const aIsSingleton = (facetDataForOrder[a.facetKey]?.uniqueValueCount ?? 0) === 1;
                const bIsSingleton = (facetDataForOrder[b.facetKey]?.uniqueValueCount ?? 0) === 1;

                if (aIsSingleton && !bIsSingleton) return -1;
                if (!aIsSingleton && bIsSingleton) return 1;
                return a.displayName.localeCompare(b.displayName);
              });
            }

            items.push({
              type: 'group',
              groupName,
              facets: facetItems,
              isExpanded: isGroupExpanded(groupName),
            });
          }
        } else {
          if (!visibleFacetKeys.includes(orderKey)) return;

          const aggregateProperties = facetData[orderKey];

          if (shouldIncludeFacet(orderKey, aggregateProperties)) {
            const properties = aggregateProperties || createEmptyAggregateProperties(orderKey);

            items.push({
              type: 'facet',
              facet: createFacetItem(orderKey, properties),
            });
          }
        }
      });

      return items;
    });
  };

  return {
    createFacetItem,
    createEmptyAggregateProperties,
    createRenderItems,
    isGroupExpanded,
    shouldIncludeFacet,
  };
}

