}

function startEditing(sectionType: string, idx: number, item: any) {
  if (isGeoLocatedImageItem(item)) return; // Only allow editing for text items
  editingSection.value = sectionType;
  editingIdx.value = idx;
  editingValue.value = item;
}

function saveEdit(section: { items: any[] }) {
  if (editingSection.value && editingIdx.value !== null) {
    const idx = editingIdx.value;
    // Update the array in place using splice
    section.items.splice(idx, 1, editingValue.value);
  }
  cancelEdit();
}

