      def fieldValue = originalFieldValue instanceof List ? new ArrayList(originalFieldValue) : originalFieldValue;

      boolean shouldRemove = false;

      // Exact match case
      if (criteria.exactValue != null) {
        if (fieldValue instanceof List) {
          // Handle array values - remove matching items from array
          int originalSize = fieldValue.size();
          fieldValue.removeIf(item -> String.valueOf(item).equals(criteria.exactValue));
          int removedCount = originalSize - fieldValue.size();

