  1514→  @Test
  1515→  void testBulkRemovePropertyValues_SuccessfulRemoval()
  1516→    throws IOException, InterruptedException {
  1517→    final String index = randomIndexName();
  1518→    elasticsearchService.createIndex(index);
  1519→
  1520→    // Create test documents with properties array
  1521→    final List<JsonNode> testData = List.of(
  1522→      objectMapper.readTree(
  1523→        """
  1524→        {
  1525→          "title": "Document 1",
  1526→          "properties": [
  1527→            {"key": "category", "value": "science"},
  1528→            {"key": "author", "value": "John Doe"},
  1529→            {"key": "category", "value": "technology"}
  1530→          ]
  1531→        }
  1532→        """
  1533→      ),
  1534→      objectMapper.readTree(
  1535→        """
  1536→        {
  1537→          "title": "Document 2",
  1538→          "properties": [
  1539→            {"key": "category", "value": "science"},
  1540→            {"key": "language", "value": "English"},
  1541→            {"key": "category", "value": "history"}
  1542→          ]
  1543→        }
  1544→        """
  1545→      ),
  1546→      objectMapper.readTree(
  1547→        """
  1548→        {
  1549→          "title": "Document 3",
  1550→          "properties": [
  1551→            {"key": "author", "value": "Jane Smith"},
  1552→            {"key": "language", "value": "French"}
  1553→          ]
  1554→        }
  1555→        """
  1556→      )
  1557→    );
  1558→
  1559→    final List<String> documentIds = List.of("doc1", "doc2", "doc3");
  1560→    elasticsearchService.insert(index, testData, documentIds);
  1561→    elasticsearchService.refresh(index);
  1562→
  1563→    // Remove "science" values from "category" property in doc1 and doc2
  1564→    final List<Object> criteria = List.of(
  1565→      new TestDeletionCriteria("category", "science", null, null)
  1566→    );
  1567→    final String taskId = elasticsearchService.bulkRemovePropertyValues(
  1568→      index,
  1569→      criteria,
  1570→      List.of("doc1", "doc2")
  1571→    );
  1572→
  1573→    Assertions.assertNotNull(taskId);
  1574→    Assertions.assertFalse(taskId.isEmpty());
  1575→
  1576→    // Wait for task completion
  1577→    Assertions.assertDoesNotThrow(() ->
  1578→      elasticsearchService.waitForTaskCompletion(taskId, 10).join()
  1579→    );
  1580→
  1581→    elasticsearchService.refresh(index);
  1582→
  1583→    // Verify results
  1584→    final SearchResponse<JsonNode> response = elasticsearchService.search(
  1585→      index,
  1586→      Query.of(b -> b.matchAll(m -> m)),
  1587→      JsonNode.class
  1588→    );
  1589→
  1590→    Assertions.assertEquals(3L, response.hits().total().value());
  1591→
  1592→    // Check doc1: should have removed "science" but kept "technology" and "author"
  1593→    final JsonNode doc1 = response
  1594→      .hits()
  1595→      .hits()
  1596→      .stream()
  1597→      .filter(hit -> hit.id().equals("doc1"))
  1598→      .findFirst()
  1599→      .orElseThrow()
  1600→      .source();
  1601→
  1602→    final JsonNode doc1Properties = doc1.get("properties");
  1603→    Assertions.assertTrue(doc1Properties.isArray());
  1604→
  1605→    boolean hasCategoryScience = false;
  1606→    boolean hasCategoryTechnology = false;
  1607→    boolean hasAuthor = false;
  1608→
  1609→    for (JsonNode prop : doc1Properties) {
  1610→      String key = prop.get("key").asText();
  1611→      String value = prop.get("value").asText();
  1612→
  1613→      if ("category".equals(key) && "science".equals(value)) {
  1614→        hasCategoryScience = true;
  1615→      }
  1616→      if ("category".equals(key) && "technology".equals(value)) {
  1617→        hasCategoryTechnology = true;
  1618→      }
  1619→      if ("author".equals(key) && "John Doe".equals(value)) {
  1620→        hasAuthor = true;
  1621→      }
  1622→    }
  1623→
  1624→    Assertions.assertFalse(
  1625→      hasCategoryScience,
  1626→      "Should have removed category:science"
  1627→    );
  1628→    Assertions.assertTrue(
  1629→      hasCategoryTechnology,
  1630→      "Should have kept category:technology"
  1631→    );
  1632→    Assertions.assertTrue(hasAuthor, "Should have kept author property");
  1633→
  1634→    // Check doc2: should have removed "science" but kept "history" and "language"
  1635→    final JsonNode doc2 = response
  1636→      .hits()
  1637→      .hits()
  1638→      .stream()
  1639→      .filter(hit -> hit.id().equals("doc2"))
  1640→      .findFirst()
  1641→      .orElseThrow()
  1642→      .source();
  1643→
  1644→    final JsonNode doc2Properties = doc2.get("properties");
  1645→    Assertions.assertTrue(doc2Properties.isArray());
  1646→
  1647→    boolean doc2HasCategoryScience = false;
  1648→    boolean doc2HasCategoryHistory = false;
  1649→    boolean doc2HasLanguage = false;
  1650→
  1651→    for (JsonNode prop : doc2Properties) {
  1652→      String key = prop.get("key").asText();
  1653→      String value = prop.get("value").asText();
  1654→
  1655→      if ("category".equals(key) && "science".equals(value)) {
  1656→        doc2HasCategoryScience = true;
  1657→      }
  1658→      if ("category".equals(key) && "history".equals(value)) {
  1659→        doc2HasCategoryHistory = true;
  1660→      }
  1661→      if ("language".equals(key) && "English".equals(value)) {
  1662→        doc2HasLanguage = true;
  1663→      }
  1664→    }
  1665→
  1666→    Assertions.assertFalse(
  1667→      doc2HasCategoryScience,
  1668→      "Should have removed category:science from doc2"
  1669→    );
  1670→    Assertions.assertTrue(
  1671→      doc2HasCategoryHistory,
  1672→      "Should have kept category:history in doc2"
  1673→    );
  1674→    Assertions.assertTrue(
  1675→      doc2HasLanguage,
  1676→      "Should have kept language property in doc2"
  1677→    );
  1678→
  1679→    // Check doc3: should be unchanged (wasn't in target document list)
  1680→    final JsonNode doc3 = response
  1681→      .hits()
  1682→      .hits()
  1683→      .stream()
  1684→      .filter(hit -> hit.id().equals("doc3"))
  1685→      .findFirst()
  1686→      .orElseThrow()
  1687→      .source();
  1688→
  1689→    final JsonNode doc3Properties = doc3.get("properties");
  1690→    Assertions.assertTrue(doc3Properties.isArray());
  1691→    Assertions.assertEquals(
  1692→      2,
  1693→      doc3Properties.size(),
  1694→      "Doc3 should be unchanged"
  1695→    );
  1696→  }
